import * as utils from './PatchbayUtils'

const fingerDefaults = {
  tapPixelsMovedThresh: 3,
  touchCircleRadiusScaler: 0.1,
  touchCircleThicknessScaler: 0.01,
  touchedLineWidth: 3
};

class Finger {
  constructor (context, inCircle, outCircle, connections, onMakeConnection, onDeleteConnection) {
    this.context = context;
    this.inCircle = inCircle;
    this.outCircle = outCircle;
    this.connections = connections;

    this.onMakeConnection = onMakeConnection;
    this.onDeleteConnection = onDeleteConnection;

    this._touchedPort = undefined;
    this._hoveredPort = undefined;

    this.circleRadius = undefined;
    this.circleThickness = undefined;

    this.x = undefined;
    this.y = undefined;
    this.xStart = undefined;
    this.yStart = undefined;
    this.down = false;
    this.didMove = false;

    this.inRadianDiff = undefined;
    this.inRadianPrev = undefined;
    this.inRadianNew = undefined;
    this.outRadianDiff = undefined;
    this.outRadianPrev = undefined;
    this.outRadianNew = undefined;

    this.justErased = false;
  }

  get touchedPort () { return this._touchedPort; }

  get hoveredPort () { return this._hoveredPort; }

  adjustToScreenSize (screenSize) {
    this.circleRadius = screenSize * fingerDefaults.touchCircleRadiusScaler;
    this.circleThickness = screenSize * fingerDefaults.touchCircleThicknessScaler;
  }

  update () {
    if (!this._touchedPort) {
      if(this.outCircle.touched){
        this.outCircle.radiansMoved = this.outRadianNew - this.outRadianPrev;
      }
      else if(this.inCircle.touched){
        this.inCircle.radiansMoved = this.inRadianNew - this.inRadianPrev;
      }
    }
    else if (this.down) {
      this.findHover();
    }
    this.inRadianPrev = this.inRadianNew;
    this.outRadianPrev = this.outRadianNew;
  }

  draw () {
    if (this.touchedPort) {
      this.drawTouchLine();
      this.drawTouchedPort();
      if (this.hoveredPort) {
        this.drawHoveredPort();
      }
    }
  }

  drawTouchedPort () {
    this.context.save();
    utils.drawCircle(
      this.context,
      this.touchedPort.x, this.touchedPort.y,
      this.circleRadius, this.circleThickness,
      utils.colors.highlight(), undefined
    );
    this.context.restore();
  }

  drawTouchLine () {
    this.context.save();
    utils.drawLine(
      this.context,
      this.touchedPort.x, this.touchedPort.y,
      this.x, this.y,
      fingerDefaults.touchedLineWidth, utils.colors.highlight()
    );
    this.context.restore();
    // then two smaller filled in circles to cover the tips of the line
    this.context.save();
    utils.drawCircle(
      this.context,
      this.touchedPort.x, this.touchedPort.y,
      fingerDefaults.touchedLineWidth, 0,
      undefined, utils.colors.highlight()
    );
    utils.drawCircle(
      this.context,
      this.x, this.y,
      fingerDefaults.touchedLineWidth, 0,
      undefined, utils.colors.highlight()
    );
    this.context.restore();
  }

  drawHoveredPort () {
    this.context.save();
    utils.drawCircle(
      this.context,
      this.hoveredPort.x, this.hoveredPort.y,
      this.circleRadius, this.circleThickness,
      utils.colors.highlight(), undefined
    );
    this.context.restore();
  }

  findHover () {
    if (!this._touchedPort) {
      return;
    }
    if (this._hoveredPort) {
      this._hoveredPort = undefined;
    }
    this._hoveredPort = this.findHoveredPort(this.outCircle);
    if (this._hoveredPort) {
      return;
    }
    this._hoveredPort = this.findHoveredPort(this.inCircle);
    if (this._hoveredPort) {
      return;
    }
  }

  findHoveredPort (circle) {
    if (circle.arcs.length === 0) {
      return;
    }
    if (this._touchedPort.type === circle.type) {
      return;
    }
    for (let arc of circle.getExpandedArcs()) {
      for (let port of arc.ports) {
        if (port.isUserTouching(this.x, this.y)) {
          return port;
        }
      }
    }
    return undefined;
  }

  touchEvent () {
    this.down = true;
    if (this.handleDeleteConnection()) {
      return;
    }
    // set all connections.hovered to false
    this.unselectAllConnections();
    this.inCircle.clearTouchedHovered();
    this.outCircle.clearTouchedHovered();
    let port = undefined;
    if(this.inCircle.isCloseTo(this.x, this.y)){
      port = this.inCircle.mouseEvent(this.x, this.y, this.inRadianNew);
    }
    else if(this.outCircle.isCloseTo(this.x, this.y)){
      port = this.outCircle.mouseEvent(this.x, this.y, this.outRadianNew);
    }
    if (port) {
      this._touchedPort = port;
    }
  }

  handleDeleteConnection () {
    for(let i in this.connections){
      const conn = this.connections[i];
      if (conn.isUserTouching(this.x, this.y)) {
        this.onDeleteConnection(conn);
        this.justErased = true;
        return true;
      }
    }
    return false;
  }

  resetOnRelease () {
    this.down = false;
    this.didMove = false;
    this.radianDown = undefined;
    this.radianDiff = undefined;
  }

  unselectAllConnections () {
    for (let name in this.connections) {
      this.connections[name].selected = false;
    }
  }

  releaseWithoutMoving (x, y) {
    this.inCircle.tapEvent();
    this.outCircle.tapEvent();
    // reset some Finger variables
    this.x = x;
    this.y = y;
    this.resetOnRelease();
    // set all connections.hovered to false
    if (!this.justErased) {
      this.unselectAllConnections();
    }
    else {
      this.justErased = false;
    }
    // find which Connection is being interacted with
    if(this._touchedPort){
      // then, find the interactive connection
      this.unselectAllConnections();
      for (let name in this.connections){
        const conn = this.connections[name];
        if (conn.inPort.isTouched() || conn.outPort.isTouched()){
          conn.selected = true;
        }
      }
    }
    this.clearCirclesTouchedHovered();
  }

  releaseEvent (x, y) {
    if (!this.didMove || utils.getDistance(this.xStart, this.yStart, x, y) < fingerDefaults.tapPixelsMovedThresh) {
      this.releaseWithoutMoving(x, y);
      return;
    }
    this.x = x;
    this.y = y;
    this.resetOnRelease();
    const touchedPort = this._touchedPort;
    const hoveredPort = this._hoveredPort;
    if (touchedPort && hoveredPort){
      this.makeConnection(hoveredPort, touchedPort);
    }

    this.unselectAllConnections();

    this.clearCirclesTouchedHovered();
  }

  clearCirclesTouchedHovered () {
    this.inCircle.clearTouchedHovered();
    this.outCircle.clearTouchedHovered();
    this._touchedPort = undefined;
    this._hoveredPort = undefined;
  }

  makeConnection (port1, port2) {
    let inPort;
    let outPort;
    if (port1.type === utils.inType){
      inPort = port1;
      outPort = port2;
    }
    else{
      inPort = port2;
      outPort = port1;
    }
    this.onMakeConnection(inPort, outPort);
  }

  moveEvent (x, y, justTouched) {
    if (justTouched) {
      this.xStart = x;
      this.yStart = y;
    }
    this.x = x;
    this.y = y;
    this.didMove = !justTouched;
    if (!justTouched){
      this.inRadianPrev = this.inRadianNew;
      this.outRadianPrev = this.outRadianNew;
    }
    this.inRadianNew = this.radiansFromCenter(this.x, this.y, utils.inType);
    this.outRadianNew = this.radiansFromCenter(this.x, this.y, utils.outType);
    if (justTouched){
      this.inRadianPrev = this.inRadianNew;
      this.outRadianPrev = this.outRadianNew;
    }
  }

  getRadian (type) {
    return type === utils.inType ? this.inRadianNew : this.outRadianNew;
  }

  radiansFromCenter (x, y, type) {
    let middleX = this.outCircle.x;
    let middleY = this.outCircle.y;
    if (type === utils.inType){
      middleX = this.inCircle.x;
      middleY = this.inCircle.y;
    }
    const yDist = Math.abs(middleY - y);
    const xDist = Math.abs(middleX - x);
    if (x > middleX){
      if (y > middleY){
        // bottom right
        return Math.atan(yDist / xDist);
      }
      else if (y < middleY){
        // top right
        return Math.atan(xDist / yDist) + (Math.PI * 1.5);
      }
      else {
        // we're touching the y line
        return 0;
      }
    }
    else if (x < middleX){
      if (y > middleY){
        // bottom left
        return Math.atan(xDist / yDist) + (Math.PI * 0.5);
      }
      else if (y < middleY){
        // top left
        return Math.atan(yDist / xDist) + Math.PI;
      }
      else {
        // we're touching the y line
        return Math.PI;
      }
    }
    else {
      //we're touching the x line
      if (y > middleY){
        return Math.PI * 0.5;
      }
      else if (y < middleY){
        return Math.PI * 1.5;
      }
      else {
        // we're touching the middle
        return 0;
      }
    }
  }
}

export { Finger }
