import * as utils from './DrawUtils'

const cordDefaults = {
  lineWidthPixels: 3,
  screenSizeScaler: 0.02,
  wobbleStep: 0.05,
  wobbleScaler: 0.2,
  crossSignThicknessScaler: 0.75,
  crossSignScaler: 0.6
};

class Connection {
  constructor (params, inPort, outPort) {
    this.params = params;
    this.name = this.constructor.generateName(inPort, outPort);

    this.inPort = inPort;
    this.outPort = outPort;

    this._selected = false;

    this.lineWidth = undefined;

    // the location and size of the delete circle
    this._x = undefined;
    this._y = undefined;
    this._radius = undefined;

    // some variables to keep track of the sinusoidal pulsing
    // when hovered for deletion
    this.wobbleCounter = Math.random() * utils.PI2;
    this.crossSignThickness = undefined;
    this.wobbledRadius = undefined;
    this.crossSignRadius = undefined;

    // colors
    this.lineFill = undefined;
    this.lineStroke = undefined;
    this.delCircleFill = undefined;
    this.delCircleStroke = undefined;
  }

  static generateName(inPort, outPort) {
    // "outArcID/inArcID_inPortID/outPortID"
    const inID = inPort.parent.id;
    const inIndex = inPort.index;
    const outID = outPort.parent.id;
    const outIndex = outPort.index;
    return String(
      inID + '/' + inIndex + '_' + outID + '/' + outIndex);
  }

  // GETTERS

  get selected () { return this._selected }

  get x () { return this._x }

  get y () { return this._y }

  get visible () { return this.inPort.visible && this.outPort.visible; }

  get sizeScaler () { return Math.min(this.inPort.sizeScaler, this.outPort.sizeScaler); }

  // SETTERS

  set selected (newVal) { this._selected = newVal; }

  update () {
    this.onScreenUpdate();

    if (this.visible) {
      // the coordinate where a deletion circle would be
      // will be halfway between the two ports
      this._x = ((this.outPort.x - this.inPort.x) / 2) + this.inPort.x;
      this._y = ((this.outPort.y - this.inPort.y) / 2) + this.inPort.y;

      this.lineWidth = cordDefaults.lineWidthPixels * this.sizeScaler;

      // colors
      this.lineFill = utils.colors.white(this.sizeScaler);
      this.lineStroke = utils.colors.white(this.sizeScaler);

      if (this._selected) {
        this.wobbleCounter = (this.wobbleCounter + cordDefaults.wobbleStep) % utils.PI2;
        const wobbleRadiusAdder = Math.sin(this.wobbleCounter) * cordDefaults.wobbleScaler * this._radius;
        const wobbledRadius = this._radius + wobbleRadiusAdder;
        this.wobbledRadius = wobbledRadius * this.sizeScaler;
        this.crossSignRadius = this.wobbledRadius * cordDefaults.crossSignScaler;
        this.crossSignThickness = this.lineWidth * cordDefaults.crossSignThicknessScaler;
        // colors
        this.lineStroke = utils.colors.red(this.sizeScaler);
        this.delCircleFill = utils.colors.white(this.sizeScaler);
        this.delCircleStroke = utils.colors.red(this.sizeScaler);
      }
    }
  }

  onScreenUpdate () {
    this._radius = this.params.screenSize * cordDefaults.screenSizeScaler;
  }

  draw () {
    if(this.visible){
      // save the context state
      this.params.context.save();
      // draw the line, from the input port to the output port
      utils.drawLine(
        this.params.context,
        this.outPort.x, this.outPort.y, this.inPort.x, this.inPort.y,
        this.lineWidth, this.lineStroke
      );
      // draw the two circles on top of that line, inside each port
      utils.drawArc(
        this.params.context,
        this.outPort.x, this.outPort.y,
        this.lineWidth, 0, utils.PI2,
        0, undefined, this.lineFill
      )
      utils.drawArc(
        this.params.context,
        this.inPort.x, this.inPort.y,
        this.lineWidth, 0, utils.PI2,
        0, undefined, this.lineFill
      )
      // restore the context state
      this.params.context.restore();

      if(this._selected){
        this.drawDeleteCircle();
      }
    }
  }

  drawDeleteCircle () {
    // save the context's state
    this.params.context.save();
    // translate to where the delete circle is
    this.params.context.translate(this._x, this._y);
    // draw the circle, with a fill and a stroke
    utils.drawArc(
      this.params.context,
      0, 0,
      this.wobbledRadius, 0, utils.PI2,
      this.lineWidth, this.delCircleStroke, this.delCircleFill
    )
    // draw the two lines that make the X
    this.params.context.rotate(utils.PI / 4);
    utils.drawLine(
      this.params.context,
      0, this.crossSignRadius, 0, -this.crossSignRadius,
      this.crossSignThickness, this.delCircleStroke
    );
    this.params.context.rotate(-utils.PI / 2);
    utils.drawLine(
      this.params.context,
      0, this.crossSignRadius, 0, -this.crossSignRadius,
      this.crossSignThickness, this.delCircleStroke
    );
    // restore the context's state
    this.params.context.restore();
  }

  isUserTouching (x, y) {
    if (!this._selected) {
      return false;
    }
    const dist = utils.getDistance(x, y, this._x, this._y);
    if (dist < this._radius) {
      return true;
    }
    return false;
  }
}

export { Connection }
