    this.updateDimensionStuff();
  }

  getArcById (id) {
    for (let arc of this._arcs) {
      if (arc.id === id) {
        return arc;
      }
    }
    return undefined;
  }

  deleteArc (id) {
    for (let i = 0; i < this._arcs.length; i++) {
      if (this._arcs[i].id === id) {
        this._arcs.splice(i, 1);
        break;
      }
    }
    if (this._arcOffset >= this._arcs.length && this._arcOffset > 0) {
      this._arcOffset = this._arcs.length - 1;
    }
    this.updateDimensionStuff();
  }

  updateDimensionStuff () {
    this.arcStartEndPoints = [];
    if (this._arcs.length === 1) {
      this.arcStartEndPoints = [
        {
          'start': utils.PI / 2,
          'end': utils.PI / 2
        }
      ];
    }
    else if (this._arcs.length === 2) {
      this.arcStartEndPoints = [
        {
          'start': utils.PI,
          'end': utils.PI2
        },
        {
          'start': 0,
          'end': utils.PI
        },
      ];
    }
    else{
      this.arcStartEndPoints[0] = {
        'start': utils.PI,
        'end': utils.PI2
      };
      const smallerWidth = utils.PI / (this._arcs.length - 1);
      for (let i = 1; i < this._arcs.length; i++) {
        this.arcStartEndPoints[i] = {
          'start': (i - 1) * smallerWidth,
          'end': i * smallerWidth
        };
      }
    }
  }

  isCloseTo (x, y) {
    const distFromCenter = utils.getDistance(this._x, this._y, x, y);
    const w = this._lineWidth / 2;
    const outerRad = (this._radiusPercentage * this.params.screenSize) + w;
    const innerRad = (this._radiusPercentage * this.params.screenSize) - w;
    if (distFromCenter <= outerRad && distFromCenter >= innerRad) {
      return true;
    }
    return false;
  }

  tapEvent () {
    if (this._touched) {
      for (let i = 0; i < this._arcs.length; i++) {
        if (this._arcs[i].touched) {
          this.startAutoMove(i);
        }
      }
    }
    this.clearTouchedHovered();
  }

  clearTouchedHovered () {
    this._touched = false;
    for (let i = 0; i < this._arcs.length; i++) {
      this._arcs[i].touched = false;
    }
  }
}

export { Circle }
