import * as utils from './DrawUtils'

const portDefaults = {
  wobbleStep: 0.07,
  wobbleAmount: 3,
  lineWidthScaler: 0.4,
  minSizeScaler: 0.25, // percentage
  fillTextFontSizeScaler: 0.2,
  fontSizeScaler: 0.4,
  touchingScaler: 1.0
};

class Port {
  constructor (params, parent, color, type, index) {
    this.params = params;
    this.context = this.params.context;

    this._parent = parent;
    this._type = type;
    this._index = index;
    this._name = "";

    this.connections = {};

    this._x = 0;
    this._y = 0;
    this.parentCircleX = 0;
    this.parentCircleY = 0;
    this.textOffset = undefined;

    this.arcRadius = undefined;
    this.arcLineWidth = undefined;

    this.wobbleCounter = Math.random() * utils.PI2;
    this.portRadius = undefined;
    this.lineWidth = undefined;

    this.strokeStyle = utils.colors.background();
    this.fillStyle = color;
    this.highlightedFillStyle = utils.colors.white();

    // the visible flag is used by Cords to determine if they should be shown
    this._visible = false;
  }

  // GETTERS

  get visible () { return this._visible }

  get x () { return this._x }

  get y () { return this._y }

  get name () { return this._name; }

  get index () { return this._index; }

  get type () { return this._type; }

  get parent () { return this._parent; }

  get radius () { return this.portRadius; }

  get sizeScaler () { return this._sizeScaler; }

  // SETTERS

  set name (name) { this._name = name; }

  update (sizeScaler, radLocation, isVisible) {
    // the Port is only visible if 1) told so by parent, and 2) is big enough
    this._visible = isVisible && sizeScaler > portDefaults.minSizeScaler;
    this._sizeScaler = !this._visible ? 0 : utils.clipValue(
      sizeScaler, portDefaults.minSizeScaler, 1);
    // Ports need an absolute coordinate on the screen
    // because their position is used to determine when they are being
    // interacted with
    this._x = this.arcRadius * Math.cos(radLocation) + this.parentCircleX;
    this._y = this.arcRadius * Math.sin(radLocation) + this.parentCircleY;
    // drawArc() radius and stroke width
    this.lineWidth = Math.floor(this.portRadius * portDefaults.lineWidthScaler);
    this.portRadius = (this.arcLineWidth / 2) * sizeScaler;
    // wobble the port's radius, if `touchedPort` is from the other circle
    this.wobbleCounter = (this.wobbleCounter + portDefaults.wobbleStep) % utils.PI2;
    if(this.params.touchedPort && this.params.touchedPort.type !== this._type) {
      this.portRadius += (Math.sin(this.wobbleCounter) * portDefaults.wobbleAmount);
    }
    // text settings
    this.textOffset = (this.arcRadius - (this.arcLineWidth / 2)) - this.lineWidth;
    this.fontSize = sizeScaler * this.arcLineWidth * portDefaults.fontSizeScaler;
  }

  onScreenUpdate (arcRadius, lineWidth, relX, relY) {
    this.arcRadius = arcRadius;
    this.arcLineWidth = lineWidth;
    this.parentCircleX = relX;
    this.parentCircleY = relY;
  }

  draw () {;
    if(this._visible){
      this.context.save();
      utils.drawArc(
        this.context,
        0, this.arcRadius,
        this.portRadius, 0, utils.PI2,
        this.lineWidth, this.strokeStyle, this.fillStyle
      )
      this.context.restore();
    }
  }

  drawName () {
    this.context.save();
    this.context.translate(this.textOffset, 0);
    if(this._x < this.parentCircleX){
      this.context.textAlign = 'left';
      this.context.rotate(utils.PI);
    }
    else{
      this.context.textAlign = 'right';
    }
    this.context.font = utils.font(this.fontSize);
    if(this.isTouched() || this.isHovered()) {
      this.context.fillStyle = this.highlightedFillStyle;
    }
    else {
      this.context.fillStyle = this.fillStyle;
    }
    this.context.fillText(
      this._name, 0, this.fontSize * portDefaults.fillTextFontSizeScaler);
    this.context.restore();
  }

  isTouched () {
    return this.params.touchedPort === this;
  }

  isHovered () {
    return this.params.hoveredPort === this;
  }

  isTouching (x, y) {
    const dist = utils.getDistance(this._x, this._y, x, y);
    if (this._visible && dist < this.portRadius) {
      return true;
    }
    return false;
  }
}

export { Port }
