  },
  {
    'name': 'Boombox',
    'inputs': ['Beat', 'Volume'],
    'outputs': ['Volume', 'Song']
  },
  {
    'name': 'Snare',
    'inputs': ['Beat', 'Volume'],
    'outputs': ['Volume']
  },
  {
    'name': 'Monome',
    'inputs': ['Pitch', 'State'],
    'outputs': []
  },
  {
    'name': 'Drawing-Bot',
    'inputs': [],
    'outputs': ['Speed', 'Direction', 'Pen-Height']
  }
];

function makeTestingNodes(patchbay) {
  // test nodes for playing with patchbay
  // create a new arc for the Tester
  const startingIndex = Math.floor(Math.random() * testingNodes.length);
  for (let index = 0; index < testingNodes.length; index++) {
    const i = (index + startingIndex) % testingNodes.length;
    const rColorIndex = i % utils.colors.palette.length;
    const color = utils.colors.palette[rColorIndex];
    const inArc = patchbay.params.inCircle.createArc(testingNodes[i].name, color, i + 2);
    patchbay.params.inCircle.addArc(inArc);
    const outArc = patchbay.params.outCircle.createArc(testingNodes[i].name, color, i + 2);
    patchbay.params.outCircle.addArc(outArc);
    const rOutputs = testingNodes[i].outputs.length;
    const totOutArcs = patchbay.params.outCircle.arcs.length;
    for (let o = 0; o < rOutputs; o++) {
      const arc = patchbay.params.outCircle.arcs[totOutArcs - 1];
      const name = testingNodes[i].outputs[o];
      const port = arc.createPort(o, name);
      arc.addPort(port);
    }
    const rInputs = testingNodes[i].inputs.length;
    const totInArcs = patchbay.params.inCircle.arcs.length;
    for (let o = 0; o < rInputs; o++) {
      const arc = patchbay.params.inCircle.arcs[totInArcs - 1];
      const name = testingNodes[i].outputs[o];
      const port = arc.createPort(o, name);
      arc.addPort(port);
    }
  }
  for (let inArc = 0; inArc < patchbay.params.inCircle.arcs.length; inArc++) {
    if(patchbay.params.inCircle.arcs[inArc].ports.length > 0) {
      for (let d = 0; d < 20; d++) {
        const outArc = Math.floor(
          Math.random() * patchbay.params.outCircle.arcs.length);
        if(patchbay.params.outCircle.arcs[outArc].ports.length > 0) {
          const inPortIndex = Math.floor(
            Math.random() * patchbay.params.inCircle.arcs[inArc].ports.length);
          const outPortIndex = Math.floor(
            Math.random() * patchbay.params.outCircle.arcs[outArc].ports.length);
          patchbay.createConnection(
            patchbay.params.inCircle.arcs[inArc].ports[inPortIndex],
            patchbay.params.outCircle.arcs[outArc].ports[outPortIndex]
          );
        }
      }
    }
  }
}

export { makeTestingNodes }
