import * as utils from './DrawUtils'
import { Arc } from './Arc'

const circleDefaults = {
  autoStepSize: 0.001,
  rotateFeedback: 0.85,
  padding: utils.PI / 100,
  outPaddingScaler: 0.5,
  isCloseToScaler: 0.4,
  highlightCuttoff: 1 / 50,
  textXOffsetScaler: 0.075,
  arcTextScaler: 0.04,
  typeFontSizeScaler: 0.025,
  labelOffsetYScaler: 2
};

class Circle {
  constructor (params, type, radiusPercentage, thickness) {
    this.params = params;

    this._radiusPercentage = radiusPercentage;
    this.relativeLineWidth = thickness;
    this._lineWidth = undefined;

    this._centerX = 0;
    this._centerY = 0;

    this._arcOffset = 0;
    this.targetOffset = 0;
    this.isAutoMoving = false;
    this.autoStepTotal = 0;
    this.autoStepCount = 0;
    this.targetArc = 0;

    this._highlighted = {
      0: true,
      1: false
    };

    this.animPercent = 0;

    this._touched = false;

    this._type = type;

    this._arcs = [];

    this.arcEndPoints = [];

    this._radiansMoved = 0;
    this.rotateFeedback = circleDefaults.rotateFeedback;

    this.padding = circleDefaults.padding;
    if (this._type === this.params.outType) {
      this.padding *= circleDefaults.outPaddingScaler;
    }
  }

  // GETTERS

  get type () { return this._type; }

  get highlighted () { return this._highlighted; }

  get lineWidth () { return this._lineWidth; }

  get arcOffset () { return this._arcOffset; }

  get radiusPercentage () { return this._radiusPercentage; }

  get touched () { return this._touched; }

  get arcs () { return this._arcs; }

  get radiansMoved () { return this._radiansMoved; }

  get centerX () { return this._centerX; }

  get centerY () { return this._centerY; }

  // SETTERS

  set radiansMoved (newVal) { this._radiansMoved = newVal; }

  set centerX (newVal) { this._centerX = newVal; }

  set centerY (newVal) { this._centerY = newVal; }

  update () {
    if (this.isAutoMoving) {
      this.updateAutoMoving();
    }
    else {
      this.rotateDrag();
    }
    const totalArcs = this._arcs.length;
    if (this._arcOffset < totalArcs) {
      this._lineWidth = Math.floor(this.relativeLineWidth * this.params.screenSize);
      for (let i = 0; i < totalArcs; i++) {
        const nonHighlightedIndex = (i + this._arcOffset) % totalArcs;
        const arcIndex = (i === 0) ? this._arcOffset : nonHighlightedIndex;
        const points = this.transpose(i);
        const sizeScaler = (this._arcOffset === arcIndex) ? 1 - this.animPercent : this.animPercent;
        this._arcs[arcIndex].update(
          points[0],
          points[1],
          this._radiusPercentage * this.params.screenSize,
          this._lineWidth,
          this.padding,
          this._highlighted[i],
          sizeScaler
        );
      }
    }
  }

  drawArcs () {
    if (this._arcs.length > 0) {
      this.params.context.save();
      this.params.context.translate(this._centerX, this._centerY);
      this.params.context.textAlign = 'center';

      // draw the TYPE text in the center of the Circle
      const typeFontSize = Math.floor(
        this.params.screenSize * circleDefaults.typeFontSizeScaler);
      let labelOffsetYScaler = circleDefaults.labelOffsetYScaler;
      labelOffsetYScaler *= (this._type === this.params.inType) ? -1 : 1;
      const typeYOffset = typeFontSize * labelOffsetYScaler;
      this.params.context.font = typeFontSize + 'px Helvetica';
      this.params.context.fillStyle = utils.colors.black();
      this.params.context.fillText(this._type.toUpperCase(), 0, typeYOffset);

      const arcTextScaler = Math.floor(this.params.screenSize * circleDefaults.arcTextScaler);

      // then draw the currently displayed arc's name
      const arc0 = this._arcs[this._arcOffset];
      const arc0FontSize = arcTextScaler * arc0.sizeScaler;
      let xOffset0 = this._lineWidth * arcTextScaler * this.animPercent;
      if (arc0.type === this.params.outType) {
        xOffset0 *= -1;
      }
      xOffset0 *= circleDefaults.textXOffsetScaler;
      this.params.context.font = arc0FontSize + 'px Helvetica';
      this.params.context.fillStyle = arc0.color(Math.min(arc0.sizeScaler, 1));
      this.params.context.fillText(arc0.name, xOffset0, 0);

      // draw the next-in-line Arc's label (fading in/out)
      const arc1 = this._arcs[(this._arcOffset + 1) % this._arcs.length];
      const arc1FontSize = arcTextScaler * arc1.sizeScaler;
      let xOffset1 = this._lineWidth * arcTextScaler * (1 - this.animPercent);
      if (arc1.type === this.params.outType) {
        xOffset1 *= -1;
      }
      xOffset1 *= circleDefaults.textXOffsetScaler;
      xOffset1 *= -1;
      this.params.context.font = arc1FontSize + 'px Helvetica';
      this.params.context.fillStyle = arc1.color(Math.min(arc1.sizeScaler, 1));
      this.params.context.fillText(arc1.name, xOffset1, 0);

      // now draw the actual child Arcs
      for (let i = 0; i < this._arcs.length; i++) {
        this._arcs[i].drawArc();
      }

      this.params.context.restore();
    }
  }

  drawNames () {
    this.params.context.save();
    this.params.context.translate(this._centerX, this._centerY);
    if (!this.params.touchedPort || this.params.touchedPort.type !== this.type) {
      for (let i = 0; i < this._arcs.length; i++) {
        if (this._arcs[i].isSelected) {
          this._arcs[i].drawName();
        }
      }
    }
    else {
      // the touched port is in this circle, so only draw it's name
      const portStart = this.params.touchedPort.parent.start;
      const portRotStep = this.params.touchedPort.parent.rotStep;
      const portRelRadsToPort = portRotStep * this.params.touchedPort.index;
      const rotateToTouchedPort = portStart + (portRotStep / 2) + portRelRadsToPort;
      this.params.context.save();
      this.params.context.rotate(rotateToTouchedPort);
      this.params.touchedPort.drawName();
      this.params.context.restore();
    }
    this.params.context.restore();
  }

  drawPorts () {
    this.params.context.save();
    this.params.context.translate(this._centerX, this._centerY);
    const totalArcs = this._arcs.length;
    const touchedPort = this.params.touchedPort;
    for (let i = 0; i < totalArcs; i++) {
      if (this._arcs[i].isSelected) {
        // don't draw the port, if it's in this Circle, and doesn't belong to this Arc
        if (touchedPort && touchedPort.type === this._type && touchedPort.parent !== this._arcs[i]) {
          continue
        }
        this._arcs[i].drawPorts();
      }
    }
    this.params.context.restore();
  }

  startAutoMove (newOffset) {
    this.targetOffset = newOffset;
    this.isAutoMoving = true;
    const moveUp = newOffset - (this._arcOffset + this._arcs.length);
    const moveDown = newOffset - this._arcOffset;
    let arcsToMove = moveUp - this.animPercent;
    if (Math.abs(moveUp) > Math.abs(moveDown)) {
      if (Math.abs(moveDown) > this._arcs.length / 2) {
        arcsToMove = moveDown + this._arcs.length + this.animPercent;
      }
      else if (moveDown === 0) {
        arcsToMove = (moveDown - this.animPercent) % this._arcs.length;
      }
      else {
        arcsToMove = (moveDown + this.animPercent) % this._arcs.length;
      }
    }
    this.autoStepTotal = arcsToMove / circleDefaults.autoStepSize;
    console.log(this.autoStepTotal);
    this.autoStepCount = 0;
  }

  updateAutoMoving () {
    if (this.autoStepCount < this.autoStepTotal) {
      this.animPercent += circleDefaults.autoStepSize;
      this.autoStepCount++;
      if (this.animPercent >= 1) {
        this._arcOffset += 1;
        if (this._arcOffset >= this._arcs.length) {
          this._arcOffset = 0;
        }
        else if (this._arcOffset<0) {
          this._arcOffset = this._arcs.length - 1;
        }
        this.animPercent = 0;
      }
      else if (this.animPercent < 0) {
        this._arcOffset -= 1;
        if (this._arcOffset >= this._arcs.length) {
          this._arcOffset = 0;
        }
        else if (this._arcOffset < 0) {
          this._arcOffset = this._arcs.length - 1;
        }
        this.animPercent = 1 + this.animPercent;
      }
    }
    else {
      this.isAutoMoving = false;
      this._arcOffset = this.targetOffset;
      this.animPercent = 0;
    }
  }

  transpose (i) {
    // `target` is the index of the neighboring arc
    // in the direction that we are rotating
    let target = i - 1;
    if (target < 0) {
      target = this._arcs.length + target;
    }
    else if (target >= this._arcs.length) {
      target = target % this._arcs.length;
    }
    // the start/end radians are pre-calculated when arcs are added/deleted
    //
    const realStart = this.arcEndPoints[i].start;
    let startDiff = this.arcEndPoints[target].start - realStart;
    const realEnd = this.arcEndPoints[i].end;
    let endDiff = this.arcEndPoints[target].end - realEnd;
    if (i === 1) {
      startDiff = startDiff * -1;
      endDiff = -utils.PI2 + endDiff;
    }

    let currentStart = (startDiff * this.animPercent) + realStart;
    let currentEnd = (endDiff * this.animPercent) + realEnd;
    if (currentStart > utils.PI2) {
      currentStart = currentStart % utils.PI2;
    }
    else if (currentStart < 0) {
      currentStart += utils.PI2;
    }
    if (currentEnd > utils.PI2) {
      currentEnd = currentEnd % utils.PI2;
    }
    else if (currentEnd < 0) {
      currentEnd += utils.PI2;
    }

    const highlightCuttoff = circleDefaults.highlightCuttoff;
    if (this.animPercent >= 0 && this.animPercent < highlightCuttoff) {
      this._highlighted = {
        0: true,
        1: true
      };
    }
    else if (this.animPercent >= highlightCuttoff && this.animPercent < 0.5) {
      this._highlighted = {
        0: true,
        1: true
      };
    }
    else if (this.animPercent >= 0.5 && this.animPercent < 1 - highlightCuttoff) {
      this._highlighted = {
        0: true,
        1: true
      };
    }
    else if (this.animPercent >= 1 - highlightCuttoff && this.animPercent < 1) {
      this._highlighted = {
        0: true,
        1: true
      };
    }
    return [currentStart, currentEnd];
  }

  mouseEvent (fingerX, fingerY, compRadian) {
    for (let i = 0; i < 2; i++) {
      const arcIndex = (i + this._arcOffset) % this._arcs.length;
      if (this._arcs[arcIndex].isTouchingPort(fingerX, fingerY)) {
        this._touched = false;
        return;
      }
    }
    this._touched = true;
    // see which arc was touched
    for (let n = 0; n < this._arcs.length; n++) {
      if (compRadian > this._arcs[n].start && compRadian < this._arcs[n].end) {
        this._arcs[n].touched = true;
        break;
      }
    }
  }

  rotateDrag () {
    if (this._radiansMoved > utils.PI / 2) {
      this._radiansMoved = utils.PI - (this._radiansMoved % utils.PI);
      this._radiansMoved *= -1;
    }
    if (!this.touched) {
      this._radiansMoved *= this.rotateFeedback;
    }

    let relativeMovement = this._radiansMoved / utils.PI2;
    if (relativeMovement > 0.5) {
      relativeMovement = 1 - (relativeMovement % 1);
    }
    else if (relativeMovement < -0.5) {
      relativeMovement = 1 + (relativeMovement % 1);
    }
    if (relativeMovement && relativeMovement < 1 && relativeMovement > -1) {
      if (Math.abs(relativeMovement) < 0) {
        this.direction = -1;
      }
      else {
        this.direction = 1;
      }
      const animStep = relativeMovement / (1 / this._arcs.length);
      this.animPercent -= animStep;

      if (this.animPercent >= 1) {
        this._arcOffset += 1;
        if (this._arcOffset >= this._arcs.length) this._arcOffset -= this._arcs.length;
        this.animPercent -= 1;
      }
      else if (this.animPercent < 0) {
        this._arcOffset -= 1;
        if (this._arcOffset < 0) this._arcOffset += this._arcs.length;
        this.animPercent += 1;
      }
    }
  }

  addArc (name, color, id) {
    const tempArc = new Arc(
      this.params,
      this,
      this._type,
      color,
      name,
      id,
      this._arcs.length
    );
    this._arcs.push(tempArc);
    this.updateDimensionStuff();
  }

  deleteArc (id) {
    for (let i = 0; i < this._arcs.length; i++) {
      if (this._arcs[i].id === id) {
        this._arcs.splice(i, 1);
        break;
      }
    }
    if (this._arcOffset >= this._arcs.length && this._arcOffset > 0) {
      this._arcOffset = this._arcs.length - 1;
    }
    this.updateDimensionStuff();
  }

  updateDimensionStuff () {
    this.arcEndPoints = [];
    if (this._arcs.length === 1) {
      this.arcEndPoints = [
        {
          'start': utils.PI / 2,
          'end': utils.PI / 2
        }
      ];
    }
    else if (this._arcs.length === 2) {
      this.arcEndPoints = [
        {
          'start': utils.PI,
          'end': utils.PI2
        },
        {
          'start': 0,
          'end': utils.PI
        },
      ];
    }
    else{
      this.arcEndPoints[0] = {
        'start': utils.PI,
        'end': utils.PI2
      };
      const smallerWidth = utils.PI / (this._arcs.length - 1);
      for (let i = 1; i < this._arcs.length; i++) {
        this.arcEndPoints[i] = {
          'start': (i - 1) * smallerWidth,
          'end': i * smallerWidth
        };
      }
    }
  }

  isCloseTo (x, y) {
    const distFromCenter = utils.getDistance(this._centerX, this._centerY, x, y);
    const w = this._lineWidth * circleDefaults.isCloseToScaler;
    const outerRad = (this._radiusPercentage * this.params.screenSize) + w;
    const innerRad = (this._radiusPercentage * this.params.screenSize) - w;
    if (distFromCenter <= outerRad && distFromCenter >= innerRad) {
      return true;
    }
    return false;
  }

  tapEvent () {
    if (this._touched) {
      for (let i = 0; i < this._arcs.length; i++) {
        if (this._arcs[i].touched) {
          this.startAutoMove(i);
        }
      }
    }
    this.clearTouchedHovered();
  }

  clearTouchedHovered () {
    this._touched = false;
    for (let i = 0; i < this._arcs.length; i++) {
      this._arcs[i].touched = false;
      for (let n = 0; n < this._arcs[i].ports.length; n++) {
        this._arcs[i].ports[n].touched = false;
        this._arcs[i].ports[n].hovered = false;
      }
    }
  }
}

export { Circle }
