import * as utils from './DrawUtils'

const cordDefaults = {
  width: 2,
  wobbleStep: 0.05,
  wobbleAmount: 2,
  deleteScaler: 0.4,
  lineWidthScaler: 0.003,
  lineWidthOpacityScaler: 2,
  circleSizeOpacityScaler: 2,
  opacityScaler: 1.5,
  deleteLineWidthScaler: 0.15,
  wobbleSizeScaler: 0.5,
  wobbleLineLengthScaler: 0.6
};

class Connection {
  constructor (params, outPort, inPort, name) {
    this.params = params;
    this.name = name;

    // references to the parent Ports (in/out)
    this.outPort = outPort;
    this.inPort = inPort;

    this._exists = true;
    this._hovered = false;

    this.x1 = undefined;
    this.y1 = undefined;
    this.x2 = undefined;
    this.y2 = undefined;
    this.stroke = undefined;
    this.lineWidth = cordDefaults.width;

    // the location and size of the delete circle
    this._deleteX = undefined;
    this._deleteY = undefined;
    this._deleteSize = this.inPort.radius * cordDefaults.deleteScaler;

    // some variables to keep track of the sinusoidal pulsing
    // when hovered for deletion
    this.wobbleCounter = Math.random() * Math.PI * 2;
    this.wobbleStep = cordDefaults.wobbleStep;
    this.wobbleAmount = cordDefaults.wobbleAmount;

    this.circleSize = undefined;
    this.lineFill = undefined;
    this.lineStroke = undefined;
    this.delCircleFill = undefined;
    this.delCircleStroke = undefined;
    this.delLineWidth = undefined;
    this.wobbleSize = undefined;
    this.wobbleLineLength = undefined;
  }

  static generateName(outID, outIndex, inID, inIndex) {
    // "outArcID/inArcID_inPortID/outPortID"
    return String(
      outID + '/' + inID + '__' + inIndex + '/' + outIndex);
  }

  // GETTERS

  get hovered () { return this._hovered }

  get deleteX () { return this._deleteX }

  get deleteY () { return this._deleteY }

  get deleteSize () { return this._deleteSize }

  get exists () { return this._exists; }

  // SETTERS

  set exists (newVal) { this._exists = newVal; }

  set hovered (newVal) { this._hovered =newVal; }

  update () {
    // set the XY coordinates based on the parent Ports' locations
    this.x1 = this.outPort.x;
    this.y1 = this.outPort.y;
    this.x2 = this.inPort.x;
    this.y2 = this.inPort.y;

    // the coordinate where a deletion circle would be
    this._deleteX = ((this.x1 - this.x2) / 2) + this.x2;
    this._deleteY = ((this.y1 - this.y2) / 2) + this.y2;

    // incase the overall size has changed, update the size variables
    this._deleteSize = this.inPort.radius * cordDefaults.deleteScaler;
    this.lineWidth = Math.max(this.params.screenSize * cordDefaults.lineWidthScaler, 2);

    // increment the sinusoidal wobbling variable
    this.wobbleCounter = (this.wobbleCounter + this.wobbleStep) % (Math.PI * 2);

    this.updateDrawSettings();
  }

  updateDrawSettings () {
    // the visible flag is set by the Arc
    if(this.outPort.visible && this.inPort.visible){
      // the opacity depends on the size of the parent ports' circles
      let opacity = Math.min(this.outPort.sizeScaler, this.inPort.sizeScaler);
      opacity = Math.min(opacity * cordDefaults.opacityScaler, 1);
      // that scaled opacity then controls all other variables for the line
      this.lineWidth = Math.floor(this.lineWidth * opacity * cordDefaults.lineWidthOpacityScaler);
      this.circleSize =  Math.floor(this.lineWidth * opacity * cordDefaults.circleSizeOpacityScaler);
      this.lineFill = utils.colors.white(opacity);
      this.lineStroke = utils.colors.white(opacity);
      if(this._hovered) {
        this.lineStroke = utils.colors.red(opacity);
        this.delCircleFill = utils.colors.white(opacity);
        this.delCircleStroke = utils.colors.red(opacity);
        const sineVal = this._deleteSize + (Math.sin(this.wobbleCounter) * this.wobbleAmount);
        this.delLineWidth = sineVal * cordDefaults.deleteLineWidthScaler;
        this.wobbleSize = sineVal * opacity * cordDefaults.wobbleSizeScaler;
        this.wobbleLineLength = this.wobbleSize * cordDefaults.wobbleLineLengthScaler;
      }
    }
  }

  draw () {
    if(this.outPort.visible && this.inPort.visible){
      // save the context state
      this.params.context.save();
      // draw the line, from the input port to the output port
      utils.drawLine(
        this.params.context,
        this.x1, this.y1, this.x2, this.y2,
        this.lineWidth, this.lineStroke
      );
      // draw the two circles on top of that line, inside each port
      utils.drawArc(
        this.params.context,
        this.x1, this.y1,
        this.circleSize, 0, utils.PI2,
        this.lineWidth, undefined, this.lineFill
      )
      utils.drawArc(
        this.params.context,
        this.x2, this.y2,
        this.circleSize, 0, utils.PI2,
        this.lineWidth, undefined, this.lineFill
      )
      // restore the context state
      this.params.context.restore();
      if(this._hovered){
        // save the context's state
        this.params.context.save();

        // translate to where the delete circle is
        this.params.context.translate(this._deleteX, this._deleteY);
        // draw the circle, with a fill and a stroke
        utils.drawArc(
          this.params.context,
          0, 0,
          this.wobbleSize, 0, utils.PI2,
          this.lineWidth, this.delCircleStroke, this.delCircleFill
        )
        // draw the two lines that make the X
        this.params.context.rotate(utils.PI / 4);
        utils.drawLine(
          this.params.context,
          0, this.wobbleLineLength, 0, -this.wobbleLineLength,
          this.delLineWidth, this.delCircleStroke
        );
        this.params.context.rotate(-utils.PI / 2);
        utils.drawLine(
          this.params.context,
          0, this.wobbleLineLength, 0, -this.wobbleLineLength,
          this.delLineWidth, this.delCircleStroke
        );
        // restore the context's state
        this.params.context.restore();
      }
    }
  }
}

export { Connection }
