import React from 'react';
import './App.css';

import { Patchbay } from './modules/Patchbay'

const screenTestScaler = 0.7;
const canvasWidth = 736 * screenTestScaler;
const canvasHeight = 414 * screenTestScaler;

class App extends React.Component {
  render() {
    return <div>
      <Animation></Animation>
    </div>;
  }
}

class Animation extends React.Component {
  constructor(props) {
    super(props);
    this.canvasRef = React.createRef();
    this.patchbay = undefined;
  }

  componentDidMount() {
    const canvas = this.canvasRef.current;
    canvas.width = canvasWidth;
    canvas.height = canvasHeight;
    const context = canvas.getContext('2d');
    this.patchbay = new Patchbay(context);
    this.patchbay.setSize(canvas.width, canvas.height);
    this.patchbay.erase();
  }

  render() {
    return (
      <div>
        <canvas ref={this.canvasRef} />
      </div>
    );
  }
}


export default App;
