import * as utils from './PatchbayUtils'

const fingerDefaults = {
  tapPixelsMovedThresh: 3
};


class Finger {
  constructor (onMakeConnection, onDeleteConnection, getGlobalTouchedPort, getGlobalHoveredPort) {

    this.x = undefined;
    this.y = undefined;
    this.xStart = undefined;
    this.yStart = undefined;
    this.xDiff = undefined;
    this.yDiff = undefined;
    this.down = false;
    this.didMove = false;

    this.inRadianDiff = undefined;
    this.inRadianPrev = undefined;
    this.inRadianNew = undefined;
    this.outRadianDiff = undefined;
    this.outRadianPrev = undefined;
    this.outRadianNew = undefined;

    this.topSide = false;
    this.justErased = false;

    this.onMakeConnection = onMakeConnection;
    this.onDeleteConnection = onDeleteConnection;

    this.getGlobalTouchedPort = getGlobalTouchedPort;
    this.getGlobalHoveredPort = getGlobalHoveredPort;
  }

  update () {
    if (this.down) {
      this.findHover();
    }
    if (this.down && this.params.outCircle.arcs.length > 1) {
      const in_radianDiff = this.inRadianNew - this.inRadianPrev;
      const out_radianDiff = this.outRadianNew - this.outRadianPrev;
      if(this.params.outCircle.touched){
        this.params.outCircle.radiansMoved = out_radianDiff;
      }
      else if(this.params.inCircle.touched){
        this.params.inCircle.radiansMoved = in_radianDiff;
      }
    }
    this.xDiff = 0;
    this.yDiff = 0;
    this.inRadianPrev = this.inRadianNew;
    this.outRadianPrev = this.outRadianNew;
  }

  findHover () {
    if (!this.params.touchedPort) {
      return;
    }
    if (this.params.hoveredPort) {
      this.params.hoveredPort = undefined;
    }
    this.findHoveredPort(this.params.outCircle);
    if (this.params.hoveredPort) {
      return;
    }
    this.findHoveredPort(this.params.inCircle);
    if (this.params.hoveredPort) {
      return;
    }
  }

  findHoveredPort (circle) {
    if (circle.arcs.length === 0) {
      return;
    }
    if (this.params.touchedPort.type === circle.type) {
      return;
    }
    for (let arc of circle.getExpandedArcs()) {
      const totalPorts = arc.ports.length;
      for (let n = 0; n < totalPorts; n++) {
        const port = arc.ports[n];
        if (port.isUserTouching(this.x, this.y)) {
          this.params.hoveredPort = port;
          break;
        }
      }
    }
  }

  touchEvent () {
    this.down = true;
    this.xDiff = 0;
    this.yDiff = 0;
    if (this.handleDeleteConnection()) {
      return;
    }
    // set all connections.hovered to false
    this.unselectAllConnections();
    this.params.inCircle.clearTouchedHovered();
    this.params.outCircle.clearTouchedHovered();
    let port = undefined;
    if(this.params.inCircle.isCloseTo(this.x, this.y)){
      port = this.params.inCircle.mouseEvent(this.x, this.y, this.inRadianNew);
    }
    else if(this.params.outCircle.isCloseTo(this.x, this.y)){
      port = this.params.outCircle.mouseEvent(this.x, this.y, this.outRadianNew);
    }
    if (port) {
      this.params.touchedPort = port;
    }
  }

  handleDeleteConnection () {
    for(let i in this.params.connections){
      const conn = this.params.connections[i];
      if (conn.isUserTouching(this.x, this.y)) {
        this.onDeleteConnection(conn);
        this.justErased = true;
        return true;
      }
    }
    return false;
  }

  resetFromNewCoords (x, y) {
    if(this.x && this.y){
      this.xDiff = x - this.x;
      this.yDiff = y - this.y;
    }
    this.x = x;
    this.y = y;
  }

  resetOnRelease () {
    this.down = false;
    this.didMove = false;
    this.radianDown = undefined;
    this.radianDiff = undefined;
  }

  unselectAllConnections () {
    for (let name in this.params.connections) {
      this.params.connections[name].selected = false;
    }
  }

  releaseWithoutMoving (x, y) {
    this.params.inCircle.tapEvent();
    this.params.outCircle.tapEvent();
    // reset some Finger variables
    this.resetFromNewCoords(x, y);
    this.resetOnRelease();
    // set all connections.hovered to false
    if (!this.justErased) {
      this.unselectAllConnections();
    }
    else {
      this.justErased = false;
    }
    // find which Connection is being interacted with
    if(this.params.touchedPort){
      // then, find the interactive connection
      this.unselectAllConnections();
      for (let name in this.params.connections){
        const conn = this.params.connections[name];
        if (conn.inPort.isTouched() || conn.outPort.isTouched()){
          conn.selected = true;
        }
      }
    }
    this.clearCirclesTouchedHovered();
  }

  releaseEvent (x, y) {
    if (!this.didMove || utils.getDistance(this.xStart, this.yStart, x, y) < fingerDefaults.tapPixelsMovedThresh) {
      this.releaseWithoutMoving(x, y);
      return;
    }
    this.resetFromNewCoords(x, y);
    this.resetOnRelease();
    const touchedPort = this.params.touchedPort;
    const hoveredPort = this.params.hoveredPort;
    if (touchedPort && hoveredPort){
      this.makeConnection(hoveredPort, touchedPort);
    }

    this.unselectAllConnections();

    this.clearCirclesTouchedHovered();
  }

  clearCirclesTouchedHovered () {
    this.params.inCircle.clearTouchedHovered();
    this.params.outCircle.clearTouchedHovered();
    this.params.touchedPort = undefined;
    this.params.hoveredPort = undefined;
  }

  makeConnection (port1, port2) {
    let inPort;
    let outPort;
    if (port1.type === utils.inType){
      inPort = port1;
      outPort = port2;
    }
    else{
      inPort = port2;
      outPort = port1;
    }
    this.onMakeConnection(inPort, outPort);
  }

  moveEvent (x, y, justTouched) {
    if (justTouched) {
      this.xStart = x;
      this.yStart = y;
    }
    this.resetFromNewCoords(x, y);
    this.didMove = !justTouched;
    // determine what side of the screen we're on
    if (this.y > this.params.middleY) this.topSide = true;
    else this.topSide = false;
    if (!justTouched){
      this.inRadianPrev = this.inRadianNew;
      this.outRadianPrev = this.outRadianNew;
    }
    this.inRadianNew = this.radiansFromCenter(this.x, this.y, utils.inType);
    this.outRadianNew = this.radiansFromCenter(this.x, this.y, utils.outType);
    if (justTouched){
      this.inRadianPrev = this.inRadianNew;
      this.outRadianPrev = this.outRadianNew;
    }
  }

  getRadian (type) {
    return type === utils.inType ? this.inRadianNew : this.outRadianNew;
  }

  radiansFromCenter (x, y, type) {
    let middleX = this.params.outCircle.x;
    let middleY = this.params.outCircle.y;
    if (type === utils.inType){
      middleX = this.params.inCircle.x;
      middleY = this.params.inCircle.y;
    }
    const yDist = Math.abs(middleY - y);
    const xDist = Math.abs(middleX - x);
    if (x > middleX){
      if (y > middleY){
        // bottom right
        return Math.atan(yDist / xDist);
      }
      else if (y < middleY){
        // top right
        return Math.atan(xDist / yDist) + (Math.PI * 1.5);
      }
      else {
        // we're touching the y line
        return 0;
      }
    }
    else if (x < middleX){
      if (y > middleY){
        // bottom left
        return Math.atan(xDist / yDist) + (Math.PI * 0.5);
      }
      else if (y < middleY){
        // top left
        return Math.atan(yDist / xDist) + Math.PI;
      }
      else {
        // we're touching the y line
        return Math.PI;
      }
    }
    else {
      //we're touching the x line
      if (y > middleY){
        return Math.PI * 0.5;
      }
      else if (y < middleY){
        return Math.PI * 1.5;
      }
      else {
        // we're touching the middle
        return 0;
      }
    }
  }
}

export { Finger }
