import * as utils from './modules/DrawUtils'

const testingNodes = [
  {
    'name': 'Phone',
    'inputs': ['Dial', 'Mic'],
    'outputs': ['Bell']
  },
  {
    'name': 'Biggie',
    'inputs': [],
    'outputs': ['Nod', 'Position']
  },
  {
    'name': 'Switch',
    'inputs': ['State'],
    'outputs': []
  },
  {
    'name': 'Toy-Pony',
    'inputs': [],
    'outputs': ['speed']
  },
  {
    'name': 'Maraca',
    'inputs': ['Intensity', 'Beat'],
    'outputs': []
  },
  {
    'name': 'Guitar',
    'inputs': ['Volume', 'Speed', 'Pitch'],
    'outputs': []
  },
  {
    'name': 'Lamp',
    'inputs': [],
    'outputs': ['Brightness', 'State']
  },
  {
    'name': 'Boombox',
    'inputs': ['Beat', 'Volume'],
    'outputs': ['Volume', 'Song']
  },
  {
    'name': 'Snare',
    'inputs': ['Beat', 'Volume'],
    'outputs': ['Volume']
  },
  {
    'name': 'Monome',
    'inputs': ['Pitch', 'State'],
    'outputs': []
  },
  {
    'name': 'Drawing-Bot',
    'inputs': [],
    'outputs': ['Speed', 'Direction', 'Pen-Height']
  }
];

function makeTestingNodes(patchbay) {
  // test nodes for playing with patchbay
  // create a new arc for the Tester
  const startingIndex = Math.floor(Math.random() * testingNodes.length);
  for (let index = 0; index < testingNodes.length; index++) {
    const i = (index + startingIndex) % testingNodes.length;
    const rColorIndex = i % utils.colors.palette.length;
    const color = utils.colors.palette[rColorIndex];
    patchbay.params.outCircle.addArc(testingNodes[i].name, color, i + 2); // ID ???
    patchbay.params.inCircle.addArc(testingNodes[i].name, color, i + 2);
    const rOutputs = testingNodes[i].outputs.length;
    const totOutArcs = patchbay.params.outCircle.arcs.length;
    for (let o = 0; o < rOutputs; o++) {
      patchbay.params.outCircle.arcs[totOutArcs - 1].addPort(o);
      patchbay.params.outCircle.arcs[totOutArcs - 1].ports[o].name = testingNodes[i].outputs[o];
    }
    const rInputs = testingNodes[i].inputs.length;
    const totInArcs = patchbay.params.inCircle.arcs.length;
    for (let o = 0; o < rInputs; o++) {
        patchbay.params.inCircle.arcs[totInArcs - 1].addPort(o);
        patchbay.params.inCircle.arcs[totInArcs - 1].ports[o].name = testingNodes[i].inputs[o];
    }
  }
  for (let c = 0; c < patchbay.params.inCircle.arcs.length; c++) {
    if(patchbay.params.inCircle.arcs[c].ports.length > 0) {
      for (let d = 0; d < 20; d++) {
        const outArc = Math.floor(
          Math.random() * patchbay.params.outCircle.arcs.length);
        if(patchbay.params.outCircle.arcs[outArc].ports.length > 0) {
          const inPort = Math.floor(
            Math.random() * patchbay.params.inCircle.arcs[c].ports.length);
          const outPort = Math.floor(
            Math.random() * patchbay.params.outCircle.arcs[outArc].ports.length);
          const connID = patchbay.createConnection(
            patchbay.params.outCircle.arcs[outArc].id,
            patchbay.params.inCircle.arcs[c].id,
            inPort,
            outPort
          );
        }
      }
    }
  }
}

export { makeTestingNodes }
