import * as utils from './DrawUtils'

const portDefaults = {
  wobbleStep: 0.07,
  wobbleAmount: 3,
  circleSizeScaler: 0.53,
  circleSizeDrawThresh: 2,
  lineWidthScaler: 0.2,
  minCircleSize: 1,
  minStrokeWidth: 4,
  fillTextFontSizeScaler: 0.2,
  radOffsetScaler: 0.05,
  fontSizeScaler: 0.4
};

class Port {
  constructor (params, parent, type, index) {
    this.params = params;

    this._parent = parent;
    this._type = type;
    this._index = index;
    this._name = "";

    this.connections = {};

    this._x = 0;
    this._y = 0;

    this._touched = false;
    this._hovered = false;

    this.sizeScaler = 0;
    this.circleRad = undefined;
    this.size = undefined;

    this.wobbleCounter = Math.random() * utils.PI2;
    this.wobbleStep = portDefaults.wobbleStep;
    this.wobbleAmount = portDefaults.wobbleAmount;

    this.circleSize = undefined;
    this.lineWidth = undefined;
    this.strokeStyle = undefined;
    this.fillStyle = undefined;
    this.shouldDraw = false;

    this.radOffset = undefined;

    // the visible flag is used by Cords to determine if they should be shown
    this._visible = false;
  }

  // GETTERS

  get visible () { return this._visible }

  get x () { return this._x }

  get y () { return this._y }

  get touched () { return this._touched; }

  get hovered () { return this._hovered; }

  get name () { return this._name; }

  get index () { return this._index; }

  get type () { return this._type; }

  get parent () { return this._parent; }

  // SETTERS

  set touched (state) { this._touched = state; }

  set hovered (state) { this._hovered = state; }

  set name (name) { this._name = name; }

  update (rad, size, radLocation, isVisible) {
    this.circleRad = rad;
    this.size = size;

    this._visible = isVisible;

    this.wobbleCounter = (this.wobbleCounter + this.wobbleStep) % utils.PI2;

    this._x = this.circleRad * Math.cos(radLocation) + this._parent.parent.centerX;
    this._y = this.circleRad * Math.sin(radLocation) + this._parent.parent.centerY;
  }

  updateDrawSettings (sizeScaler) {
    this.sizeScaler = sizeScaler;
    const tempSize = this.size * this.sizeScaler * portDefaults.circleSizeScaler;
    this.shouldDraw = false;
    if(tempSize > portDefaults.circleSizeDrawThresh){
      this.shouldDraw = true;
      // the size of the Port's circle
      this.circleSize = Number(tempSize);
      if(this.params.touchedPort && this.params.touchedPort.type != this._type) {
        this.circleSize += (Math.sin(this.wobbleCounter) * this.wobbleAmount);
      }
      this.circleSize = Math.max(this.circleSize, portDefaults.minCircleSize);

      // colors
      this.strokeStyle = undefined;
      if(tempSize > portDefaults.minStrokeWidth) {
        this.strokeStyle = utils.colors.grey();
      }
      this.fillStyle = this._parent.color();
      this.lineWidth = Math.floor(tempSize * portDefaults.lineWidthScaler);
    }

    // settings for drawing the name
    this.radOffset = this._parent.radius - (this.params.scaler * portDefaults.radOffsetScaler);
    this.fontSize = Math.floor(this.sizeScaler * this.size * portDefaults.fontSizeScaler);
  }

  draw (sizeScaler) {
    // TODO: updating drawing setting should me moved to the update function
    this.updateDrawSettings(sizeScaler);
    if(this.shouldDraw){
      this.params.context.save();
      utils.drawArc(
        this.params.context,
        0, this.circleRad,
        this.circleSize, 0, utils.PI2,
        this.lineWidth, this.strokeStyle, this.fillStyle
      )
      this.params.context.restore();
    }
  }

  drawName () {
    this.params.context.save();
    this.params.context.translate(this.radOffset, 0);

    if(this._x < this._parent.parent.centerX){
      this.params.context.textAlign = 'left';
      this.params.context.rotate(Math.PI);
    }
    else{
      this.params.context.textAlign = 'right';
    }
    this.params.context.font = this.fontSize + 'px Helvetica';
    if(this._touched || this._hovered) {
      this.params.context.fillStyle = utils.colors.white();
    }
    else {
      this.params.context.fillStyle = this._parent.color();
    }

    this.params.context.fillText(
      this._name, 0, this.fontSize * portDefaults.fillTextFontSizeScaler);
    this.params.context.restore();
  }
}

export { Port }
