const PI = Math.PI;
const PI2 = Math.PI * 2;


const colors = {
  black: (opacity = 1.0) => `rgba(0, 0, 0, ${opacity})`,
  white: (opacity = 1.0) => `rgba(255, 255, 255, ${opacity})`,
  red: (opacity = 1.0) => `rgba(226, 39, 39, ${opacity})`,
  green: (opacity = 1.0) => `rgba(100, 255, 100, ${opacity})`,
  background: (opacity = 1.0) => `rgba(79, 79, 79, ${opacity})`, // grey
  palette: [
    (opacity = 1.0) => `rgba(176, 79, 79, ${opacity})`,
    (opacity = 1.0) => `rgba(176, 79, 176, ${opacity})`,
    (opacity = 1.0) => `rgba(43, 211, 252, ${opacity})`,
    (opacity = 1.0) => `rgba(176, 176, 79, ${opacity})`,
    (opacity = 1.0) => `rgba(252, 120, 43, ${opacity})`,
    (opacity = 1.0) => `rgba(176, 176, 176, ${opacity})`,
    (opacity = 1.0) => `rgba(30, 147, 175, ${opacity})`
  ]
}

const drawLine = (context, x1, y1, x2, y2, width, stroke = undefined) => {
  context.beginPath();
  continueLine(context, x1, y1, x2, y2, width, stroke);
}

const continueLine = (context, x1, y1, x2, y2, width, stroke = undefined) => {
  if (width) {
    context.lineWidth = width;
  }
  if (stroke) {
    context.strokeStyle = stroke;
  }
  context.moveTo(x1, y1);
  context.lineTo(x2, y2);
  if (stroke) {
    context.stroke();
  }
}

const drawArc = (context, x, y, radius, start, end, width, stroke = undefined, fill = undefined) => {
  context.beginPath();
  if (stroke) context.strokeStyle = stroke;
  if (fill) context.fillStyle = fill;
  context.lineWidth = width;
  context.arc(x, y, radius, start, end, false);
  if (stroke) context.stroke();
  if (fill) context.fill();
}

const getDistance = (x1, y1, x2, y2) => {
  var xDiff = x1 - x2;
  var yDiff = y1 - y2;
  return Math.sqrt(xDiff * xDiff + yDiff * yDiff);
}

export {
  PI,
  PI2,
  colors,
  drawLine,
  continueLine,
  drawArc,
  getDistance
}
