    this._hoveredPort = undefined;
    this._inType = 'in';
    this._outType = 'out';
    this._deleteType = 'delete';
    this._connections = {};
    this._inCircle = undefined;
    this._outCircle = undefined;
    this._testingGUI = false;
  }

  update (newWidth) {
    // all values are based off of the screen's width
    if (newWidth && newWidth > 0) {
      this._width = newWidth;
      this._height = Math.floor(this._width * (925 / 1200)) * .97;
      this._middleX = this._width / 2;
      this._middleY = this._height / 2;
      this._scaler = Math.min(this._width, this._height * 1.2) * .9;
    }
  }

  addConnection (name, connection) {
    this._connections[name] = connection;
  }

  deleteConnection (index) {
    //
  }

  getPort (parentID, portID, type) {
    const circle = type === this.inType ? this.inCircle : this.outCircle;
