      if (circle.arcs[i].id === parentID) {
        return circle.arcs[i].ports[portID];
      }
    }
  }

  // GETTERS

  get testingGUI () { return this._testingGUI; }

  get inCircle () { return this._inCircle; }

  get outCircle () { return this._outCircle; }

  get connections () { return this._connections; }

  get inType () { return this._inType; }

