    this.shouldDrawGutter = this._type === this.params.inType;
  }

  onScreenUpdate (radius, lineWidth, circleX, circleY) {
    this.radius = radius;
    this.lineWidth = lineWidth;
    for (let port of this._ports) {
      port.onScreenUpdate(radius, lineWidth, circleX, circleY);
    }
  }

  update (start, end, isSelected, sizeScaler) {
    // update this Arc's visual draw settings
    this._sizeScaler = this.modifySizeScaler(sizeScaler);
    this._isSelected = isSelected;
    this.start = (start + this.angleOffset) % utils.PI2;
    this.end = (end + this.angleOffset) % utils.PI2;
    if (this.end < this.start) {
      this.end += utils.PI2;
    }
    if (this._ports.length > 0) {
      this.arcLineWidth = this.lineWidth * arcDefaults.arcWidthScaler;
    }
    else {
      this.arcLineWidth = this.lineWidth * arcDefaults.emptyArcScaler;
    }
    this.arcLineWidth = utils.clipValue(this.arcLineWidth, 1);
    this.updatePorts();
    if (this.shouldDrawGutter) {
      this.updateGutter();
    }
  }

  updateGutter() {
    // if it's an Input Arc and is selected, give it an inner grey "gutter"
    this.gutterLineWidth = Math.max(
      this.lineWidth * arcDefaults.gutterLineWidthScaler, 1);
    this.gutterStart = this.start + (utils.PI2 * arcDefaults.gutterScaler);
    this.gutterEnd = this.end - (utils.PI2 * arcDefaults.gutterScaler);
  }

  updatePorts () {
    // update each Port's visual draw settings
    this.rotStep = (this.end - this.start) / this._ports.length;
    for (let i = 0; i < this._ports.length; i++) {
      const radLocation = this.start + (this.rotStep * i) + (this.rotStep / 2);
      this._ports[i].update(this._sizeScaler, radLocation, this._isSelected);
    }
  }

  getTouchedPort (x, y) {
    for (let i = 0; i < this._ports.length; i++) {
      if (this._ports[i].isUserTouching(x, y)) {
        return this._ports[i];
      }
    }
    return undefined;
  }

  drawArc () {
    // save the context's state
    this.context.save();
    utils.drawArc(
      this.context,
      0, 0, this.radius, this.start, this.end,
      this.arcLineWidth, this._color(), undefined
    )
