        this._ports[i].draw(this._sizeScaler);
      }
      this.context.rotate(this.rotStep);
    }
    this.context.restore();
  }

  modifySizeScaler (sizeScaler) {
    sizeScaler = utils.clipValue(
      sizeScaler * arcDefaults.sizeScalerMultiplier,
      0,
      arcDefaults.sizeScalerThresh
    );
    return Math.pow(sizeScaler / arcDefaults.sizeScalerThresh, 2);
  }
}

