    'name': 'Drawing-Bot',
    'inputs': [],
    'outputs': ['Speed', 'Direction', 'Pen-Height']
  }
];

function makeTestingNodes(params) {
  // test nodes for playing with patchbay
  // create a new arc for the Tester
  const startingIndex = Math.floor(Math.random() * testingGUI_Nodes.length);
  for (let index = 0; index < testingGUI_Nodes.length; index++) {
    const i = (index + startingIndex) % testingGUI_Nodes.length;
    const rColorIndex = i % utils.colors.palette.length;
    const color = utils.colors.palette[rColorIndex];
    params.outCircle.addArc(testingGUI_Nodes[i].name, color, i + 2); // ID ???
    params.inCircle.addArc(testingGUI_Nodes[i].name, color, i + 2);
    const rOutputs = testingGUI_Nodes[i].outputs.length;
    const totOutArcs = params.outCircle.arcs.length;
    for (let o = 0; o < rOutputs; o++) {
      params.outCircle.arcs[totOutArcs - 1].addPort(o);
      params.outCircle.arcs[totOutArcs - 1].ports[o].name = testingGUI_Nodes[i].outputs[o];
    }
    const rInputs = testingGUI_Nodes[i].inputs.length;
    const totInArcs = params.inCircle.arcs.length;
    for (let o = 0; o < rInputs; o++) {
        params.inCircle.arcs[totInArcs - 1].addPort(o);
        params.inCircle.arcs[totInArcs - 1].ports[o].name = testingGUI_Nodes[i].inputs[o];
    }
  }

  for (var c=0;c<inCircle.arcs.length;c++) {
    var inArc = c;
    if(inCircle.arcs[inArc].ports.length>0) {
      for (var d=0;d<20;d++) {
        var outArc = Math.floor(Math.random()*outCircle.arcs.length);
        if(outCircle.arcs[outArc].ports.length>0) {
          var inPort = Math.floor(Math.random()*inCircle.arcs[inArc].ports.length);
          var outPort = Math.floor(Math.random()*outCircle.arcs[outArc].ports.length);
          sendRoute(outCircle.arcs[outArc].id,inCircle.arcs[inArc].id,inPort,outPort);
          console.log('port made');
        }
      }
    }
  }
}

export { testingNodes }
