import React from 'react';
import './App.css';

import { Patchbay } from './modules/Patchbay'
import { makeTestingNodes} from './modules/TestParams'

// TODO: get screen values from React-Native methods when on mobile
const screenTestScaler = 0.8;
const canvasWidth = Math.floor(736 * screenTestScaler);
const canvasHeight = Math.floor(414 * screenTestScaler);

class App extends React.Component {
  render() {
    return <div>
      <Animation></Animation>
    </div>;
  }
}

class Animation extends React.Component {
  constructor (props) {
    super(props);
    this.canvasRef = React.createRef();
    this.patchbay = undefined;
  }

  componentDidMount () {
    const canvas = this.canvasRef.current;
    canvas.width = canvasWidth;
    canvas.height = canvasHeight;
    this.patchbay = new Patchbay(canvas);
    this.patchbay.setSize(canvas.width, canvas.height);
    makeTestingNodes(this.patchbay);
    this.patchbay.drawLoop();
  }

  handleUserEvent (event) {
    console.log(event);
  }

  render() {
    return (
      <div
        onMouseDown={(event) => this.handleUserEvent(event, 'onMouseDown')}
        onMouseMove={(event) => this.handleUserEvent(event, 'onMouseMove')}
        onClick={(event) => this.handleUserEvent(event, 'onClick')}
        onMouseOut={(event) => this.handleUserEvent(event, 'onMouseOut')}
        onMouseLeave={(event) => this.handleUserEvent(event, 'onMouseLeave')}
        onMouseUp={(event) => this.handleUserEvent(event, 'onMouseUp')}
      >
        <canvas ref={this.canvasRef} />
      </div>
    );
  }
}


export default App;
