import * as utils from './DrawUtils'
import { AppParams } from './AppParams'
import { Connection } from './Connection'
import { Finger } from './Finger'
import { Circle } from './Circle'


const userEventTypes = {
  touch: 'touch',
  move: 'move',
  release: 'release'
};


const patchbayDefaults = {
  tempThickness: 0.1,
  screenPercentage: 0.25,
  touchCircleThicknessScaler: 0.01,
  touchedLineMaxWidth: 4,
  touchedLineMinWidth: 2,
  touchedTipPreScaler: 1.25,
  touchedTipPostScaler: 0.1,
  hoveredCircleWidthScaler: 0.1,
  touchCircleRadiusScaler: 0.1
};

class Patchbay {
  constructor (context) {
    this.params = new AppParams(context);
    this.params.inCircle = new Circle(
      this.params,
      this.params.inType,
      patchbayDefaults.screenPercentage,
      patchbayDefaults.tempThickness
    );
    this.params.outCircle = new Circle(
      this.params,
      this.params.outType,
      patchbayDefaults.screenPercentage,
      patchbayDefaults.tempThickness
    );
    this.finger = new Finger(
      this.params, this.createConnection, this.params.deleteConnection
    );
    this.reqAnimFrame = undefined;
  }

  drawLoop () {
    if (this.reqAnimFrame) {
      cancelAnimationFrame(this.reqAnimFrame);
      this.reqAnimFrame = undefined;
    }
    this.finger.update();
    this.params.outCircle.update();
    this.params.inCircle.update();
    for(let i in this.params.connections){
      this.params.connections[i].update();
    }

    this.params.context.save();
    this.clearCanvas();

    this.params.outCircle.drawArcs();
    this.params.inCircle.drawArcs();

    this.params.outCircle.drawNames();
    this.params.inCircle.drawNames();

    this.params.outCircle.drawPorts();
    this.params.inCircle.drawPorts();

    for(let i in this.params.connections){
      this.params.connections[i].draw();
    }

    const circleRadius = this.params.screenSize * patchbayDefaults.touchCircleRadiusScaler;
    const circleThickness = this.params.screenSize * patchbayDefaults.touchCircleThicknessScaler;
    this.drawTouchLine();
    this.drawTouchedPort(circleRadius, circleThickness);
    this.drawHoveredPort(circleRadius, circleThickness);

    this.params.context.restore();
    this.reqAnimFrame = requestAnimationFrame(() => this.drawLoop());
  }

  drawTouchedPort (circleRadius, circleThickness) {
    if (!this.params.touchedPort) {
      return;
    }
    this.params.context.save();
    utils.drawArc(
      this.params.context,
      this.params.touchedPort.x, this.params.touchedPort.y,
      circleRadius, 0, utils.PI2, circleThickness,
      utils.colors.white(), undefined
    );
    this.params.context.restore();
  }

  drawTouchLine () {
    if (!this.params.touchedPort) {
      return;
    }
    let touchedLineWidth = Math.sin(this.params.touchedPort.wobbleCounter);
    touchedLineWidth = (touchedLineWidth + 1) / 2; // map to 0-1
    touchedLineWidth *= patchbayDefaults.touchedLineMaxWidth - patchbayDefaults.touchedLineMinWidth;
    touchedLineWidth += patchbayDefaults.touchedLineMinWidth;
    this.params.context.save();
    utils.drawLine(
      this.params.context,
      this.params.touchedPort.x, this.params.touchedPort.y,
      this.finger.x, this.finger.y,
      touchedLineWidth, utils.colors.green()
    );
    this.params.context.restore();
    // then two smaller filled in circles to cover the tips of the line
    this.params.context.save();
    utils.drawArc(
      this.params.context,
      this.params.touchedPort.x, this.params.touchedPort.y,
      touchedLineWidth * 2, 0, utils.PI2, 0,
      undefined, utils.colors.white()
    );
    utils.drawArc(
      this.params.context,
      this.finger.x, this.finger.y,
      touchedLineWidth * 2, 0, utils.PI2, 0,
      undefined, utils.colors.white()
    );
    this.params.context.restore();
  }

  drawHoveredPort (circleRadius, circleThickness) {
    if (!this.params.hoveredPort) {
      return;
    }
    this.params.context.save();
    utils.drawArc(
      this.params.context,
      this.params.hoveredPort.x, this.params.hoveredPort.y,
      circleRadius, 0, utils.PI2, circleThickness,
      utils.colors.green(), undefined
    );
    this.params.context.restore();
  }

  setSize (width, height) {
    this.params.width = width;
    this.params.height = height;
  }

  clearCanvas() {
    this.params.context.fillStyle = utils.colors.background();
    this.params.context.fillRect(0, 0, this.params.width, this.params.height);
  }

  createConnection (inPort, outPort) {
    const tempName = Connection.generateName(inPort, outPort);
    if (!this.params.connections[tempName]) {
      const newConnection = new Connection(this.params, inPort, outPort, tempName);
      this.params.addConnection(tempName, newConnection);
    }
    else {
      // TODO: not sure what this does, seems to be left over from thesis
      this.params.connections[tempName].exists = true;
    }
    return tempName;
  }

  eraseNodeFromConnections (id) {
    for (let n in this.params.connections) {
      const conn = this.params.connections[n];
      if (conn.outPort.parent.id === id || conn.inPort.parent.id === id) {
        this.params.deleteConnection(conn);
      }
    }
  }

  handleUserEvent (type, x, y) {
    if (type === userEventTypes.touch) {
      const justTouched = true;
      this.finger.moveEvent(x, y, justTouched);
      this.finger.touchEvent();
    }
    else if (type === userEventTypes.move) {
      const justTouched = false;
      this.finger.moveEvent(x, y, justTouched);
    }
    else if (type === userEventTypes.release) {
      this.finger.releaseEvent(x, y);
    }
  }
}

export { Patchbay, userEventTypes }
