    this.end = undefined;
    this.radius = undefined;
    this.portSize = undefined;
    this.arcLineWidth = undefined;

    this.gutterStart = undefined;
    this.gutterEnd = undefined;
    this.gutterLineWidth = undefined;

    this.test = true;
  }

  // GETTERS

  get isSelected () { return this._isSelected; }

  get parent () { return this._parent; }

  get type () { return this._type; }

  get color () { return this._color; }

  get name () { return this._name; }

  get id () { return this._id; }

  get index () { return this._index; }

  get ports () { return this._ports; }

  get focused () { return this._focused; }

  get touched () { return this._touched; }

  get sizeScaler () { return this._sizeScaler; }

  // SETTERS

  set focused (newVal) { this._focused = newVal; }

  set touched (newVal) { this._touched = newVal; }

  addPort (index) {
    // a port is simply labelled by it's index
    const tempPort = new Port(this.params, this, this._type, index);
    this._ports[index] = tempPort;
  }

  handleMeta (data) {
    // all of an Arc's ports are created all at once
    if (this._ports.length === 0) {
      const totalNewPorts = Number(data.totalPorts[this._type]);
      for (let i = 0; i < totalNewPorts; i++) {
        this.addPort(i);
      }
    }
    // if ports already exist, then we are updated the names of the ports
    else if (this.test) {
      // loop through all ports of this type (in or out)
      for (let i in data.ports[this._type]) {
        const dataPort = data.ports[this._type][i];
        // TODO: not sure when this would ever be false...
        if (dataPort) {
          // loop through all this Arc's previously saved ports
          for (let p = 0;p < this._ports.length; p++) {
            if (this._ports[p].index === dataPort.index) {
              // update the name
              this._ports[p].name = dataPort.name;
              break;
            }
          }
        }
      }
    }
  }

  update (start, end, radius, portSize, padding, isSelected, sizeScaler) {
    // update this Arc's visual draw settings
    this._sizeScaler = this.modifySizeScaler(sizeScaler);
    this.start = start + padding;
    this.end = end - padding;
    if (this._parent.type === this.params.outType) {
      if (this.params.width < this.params.height) {
        this.start = (this.start + (utils.PI * 1.75)) % utils.PI2;
        this.end = (this.end + (utils.PI * 1.75)) % utils.PI2;
      }
      else {
        this.start = (this.start + (utils.PI * .25)) % utils.PI2;
        this.end = (this.end + (utils.PI * .25)) % utils.PI2;
      }
    }
    else {
      if (this.params.width < this.params.height) {
        this.start = (this.start + (utils.PI * .75)) % utils.PI2;
        this.end = (this.end + (utils.PI * .75)) % utils.PI2;
      }
      else {
        this.start = (this.start + (utils.PI * 1.25)) % utils.PI2;
        this.end = (this.end + (utils.PI * 1.25)) % utils.PI2;
      }
    }
    if (this.params.height < this.params.width) {
      this.start = (this.start + utils.PI * 1.5) % utils.PI2;
      this.end = (this.end + utils.PI * 1.5) % utils.PI2;
    }
    this.radius = radius;
    this.portSize = portSize;
    this._isSelected = isSelected;
    if (this.end < this.start) {
      this.end += utils.PI2;
