    // if it's an Input Arc and is selected, give it an inner grey "gutter"
    this.gutterStart = this.start + (utils.PI * arcDefaults.gutterScaler);
    this.gutterEnd = this.end - (utils.PI * arcDefaults.gutterScaler);
    this.gutterLineWidth = undefined;
    if (this._isSelected) {
