        this.gutterLineWidth = Math.max(
          this._parent.lineWidth * arcDefaults.gutterLineWidthScaler, 1);
      }
    }
  }

  updatePorts () {
    // update each Port's visual draw settings
    this.rotStep = (this.end - this.start) / this._ports.length;
    for (let i = 0;i < this._ports.length; i++) {
      if (this._ports[i]) {
        // the visible flag is used by Cords to determine if they should be shown
        const radLocation = this.start + (this.rotStep * i) + (this.rotStep / 2);
        const isVisible = Boolean(this._isSelected);
        this._ports[i].update(this.radius, this.portSize, radLocation, isVisible);
      }
