        this.params.context,
        0, 0, this.radius, this.gutterStart, this.gutterEnd,
        this.gutterLineWidth, utils.colors.background(), undefined
      )
    }
    this.params.context.restore();
  }

  drawName () {
    this.params.context.save();
    this.params.context.rotate(this.start + (this.rotStep / 2));
    for (let i = 0;i < this._ports.length; i++) {
      if (this._ports[i]) {
        this._ports[i].drawName();
        this.params.context.rotate(this.rotStep);
      }
    }
    this.params.context.restore();
  }

