    // draw the child Ports
    this.params.context.save();
    this.params.context.rotate((this.start + (this.rotStep / 2)) - (utils.PI * 0.5));
    for (let i = 0; i < this._ports.length; i++) {
      if (this._ports[i]) {
        this._ports[i].draw(this._sizeScaler);
        this.params.context.rotate(this.rotStep);
      }
    }
    this.params.context.restore();
  }

  modifySizeScaler (sizeScaler) {
    if (sizeScaler < 0) sizeScaler = 0;
    sizeScaler *= arcDefaults.sizeScalerMultiplier;
    if (sizeScaler > arcDefaults.sizeScalerThresh) {
      sizeScaler = arcDefaults.sizeScalerThresh;
    }
    sizeScaler = sizeScaler / arcDefaults.sizeScalerThresh;
    sizeScaler = Math.pow(this._sizeScaler, 2);
    return sizeScaler;
  }
}

export { Arc }
