import { Connection } from './Connection'

const appParamsDefaults = {
  globalScalerHeightMultiplier: 1.2,
  globalScalerMultiplier: 0.9,
  circleCenterXOffsetScaler: 0.3,
  circleCenterYOffsetScaler: 0.1
};

