    can update, and all objects holding a reference to is can use it's values
  */
  constructor (context) {
    this._context = context;
    this._width = 1;
    this._height = 1;
    this._middleX = 1;
    this._middleY = 1;
    this._screenSize = 1; // previously known as `usedSize`
    this._touchedPort = undefined;
    this._hoveredPort = undefined;
    this._inType = 'in';
    this._outType = 'out';
    this._deleteType = 'delete';
    this._connections = {};
    this._inCircle = undefined;
    this._outCircle = undefined;
    this._testingGUI = false;
  }

  createConnection (inPort, outPort) {
    const name = Connection.generateName(inPort, outPort);
