    for (let i = 0; i < circle.arcs.length; i++) {
      if (circle.arcs[i].id === parentID) {
        return circle.arcs[i].ports[portID];
      }
    }
    return undefined;
  }

  // GETTERS

  get testingGUI () { return this._testingGUI; }

  get inCircle () { return this._inCircle; }

  get outCircle () { return this._outCircle; }

  get connections () { return this._connections; }

  get inType () { return this._inType; }

  get outType () { return this._outType; }

  get deleteType () { return this._deleteType; }

  get context () { return this._context; }

  get width () { return this._width; }

  get height () { return this._height; }

  get middleX () { return this._middleX; }

  get middleY () { return this._middleY; }

  get screenSize () { return this._screenSize; }

  get touchedPort () { return this._touchedPort; }

  get hoveredPort () { return this._hoveredPort; }

  // SETTERS

  set touchedPort (newPort) { this._touchedPort = newPort; }

  set hoveredPort (newPort) { this._hoveredPort = newPort; }

  set inCircle (circle) { this._inCircle = circle; }

  set outCircle (circle) { this._outCircle = circle; }

  set width (newVal) {
    this._width = newVal;
    this.updateScreenPositions();
  }

  set height (newVal) {
    this._height = newVal;
    this.updateScreenPositions();
  }

  updateScreenPositions () {
    this._middleX = this._width / 2;
    this._middleY = this._height / 2;
    this._screenSize = Math.min(
      this._width,
      this._height * appParamsDefaults.globalScalerHeightMultiplier
    );
    this._screenSize *= appParamsDefaults.globalScalerMultiplier;
    const xOffset = this._width * appParamsDefaults.circleCenterXOffsetScaler;
    const yOffset = this._height * appParamsDefaults.circleCenterYOffsetScaler;
    this.inCircle.centerX = this._middleX - xOffset;
    this.outCircle.centerX = this._middleX + xOffset;
    this.inCircle.centerY = this._middleY - yOffset;
    this.outCircle.centerY = this._middleY + yOffset;
