import * as utils from './DrawUtils'
import { Connection } from './Connection'

const fingerDefaults = {
  portHoverDistanceThresh: 0.75
};


class Finger {
  constructor (params) {
    this.params = params;

    this.x = undefined;
    this.y = undefined;
    this.xDiff = undefined;
    this.yDiff = undefined;
    this.down = false;
    this.didDrag = false;

    this.inRadianDiff = undefined;
    this.inRadianPrev = undefined;
    this.inRadianNew = undefined;
    this.outRadianDiff = undefined;
    this.outRadianPrev = undefined;
    this.outRadianNew = undefined;

    this.topSide = false;
    this.justErased = false;

    // TODO: these should be functions passed in during instantiation
    this.onMakeConnection = (outID, outIndex, inID, inIndex) => {
      const name = Connection.generateName(outID, outIndex, inID, inIndex);
      console.log('call to delete connection: ' + name)
    }
    this.onDeleteConnection = (outID, outIndex, inID, inIndex) => {
      const name = Connection.generateName(outID, outIndex, inID, inIndex);
      console.log('call to delete connection: ' + name)
    }
  }

  update () {
    if (this.down || this.params.touchedPort) {
      this.findHover();
    }
    if (this.down && this.params.outCircle.arcs.length > 1) {
      const in_radianDiff = this.inRadianNew - this.inRadianPrev;
      const out_radianDiff = this.outRadianNew - this.outRadianPrev;
      if(this.params.outCircle.touched){
        this.params.outCircle.radiansMoved = out_radianDiff;
      }
      else if(this.params.inCircle.touched){
        this.params.inCircle.radiansMoved = in_radianDiff;
      }
    }
    this.xDiff = 0;
    this.yDiff = 0;
    this.inRadianPrev = this.inRadianNew;
    this.outRadianPrev = this.outRadianNew;
  }

  findHover (fingerX, fingerY) {
    if (this.params.hoveredPort) {
      this.params.hoveredPort.hovered = false;
      this.params.hoveredPort = undefined;
    }
    const outCircle = this.params.outCircle;
    const inCircle = this.params.inCircle;
    const touchedPort = this.params.touchedPort;
    const hoveredPort = this.params.hoveredPort;
    if (outCircle.touched || inCircle.touched) {
      return;
    }
    if (outCircle.arcs.length > 0) {
      if ((touchedPort && touchedPort.type === this.params.inType) || !touchedPort) {
        for (let i = 0; i < 2; i++) {
          const index = (i + outCircle.arcOffset) % outCircle.arcs.length;
          const len = outCircle.arcs[index].ports.length;
          for (let n = 0; n < len; n++) {
            const port = outCircle.arcs[index].ports[n];
            if (port.isTouching(fingerX, fingerY)) {
              this.params.outCircle.arcs[index].ports[n].hovered = true;
              this.params.hoveredPort = this.params.outCircle.arcs[index].ports[n];
              break;
            }
            else {
              this.params.outCircle.arcs[index].ports[n].hovered = false;
            }
          }
        }
      }
    }
    if (this.params.hoveredPort || this.params.inCircle.arcs.length <= 0) {
      return;
    }
    if (!this.params.touchedPoint || this.params.touchedPort.type === this.params.outType) {
      //loop through inner seen ports
      for (var i = 0; i < 2; i++) {
        if (!hoveredPort && inCircle.highlighted[String(i)]) {
          var index = (i + inCircle.arcOffset) % inCircle.arcs.length;
          var len = inCircle.arcs[index].ports.length;
          for (var n = 0; n < len; n++) {
            const port = this.params.inCircle.arcs[index].ports[n];
            const absDiff = utils.getDistance(port.x, port.y, fingerX, fingerY);
            if (absDiff < port.size / 2) {
              this.params.inCircle.arcs[index].ports[n].hovered = true;
              this.params.hoveredPort = this.params.inCircle.arcs[index].ports[n];
              break;
            }
            else {
              this.params.inCircle.arcs[index].ports[n].hovered = false;
            }
          }
        }
      }
    }
  }

  touchEvent () {
    this.down = true;
    this.xDiff = 0;
    this.yDiff = 0;
    if (this.handleDeleteConnection()) {
      return;
    }
    this.params.inCircle.clearTouchedHovered();
    this.params.outCircle.clearTouchedHovered();
    if(this.params.inCircle.isCloseTo(this.x, this.y)){
      this.params.inCircle.mouseEvent(this.x, this.y, this.inRadianNew);
    }
    else if(this.params.outCircle.isCloseTo(this.x, this.y)){
      this.params.outCircle.mouseEvent(this.x, this.y, this.outRadianNew);
    }
  }

  handleDeleteConnection () {
    for(let i in this.params.connections){
      const conn = this.params.connections[i];
      if(conn.hovered){
        const tempDist = utils.getDistance(
          this.x, this.y, conn.deleteX, conn.deleteY);
        if(tempDist < conn.deleteSize){
          const outputID = conn.outPort.parent.id;
          const outputIndex = conn.outPort.index;
          const inputID = conn.inPort.parent.id;
          const inputIndex = conn.inPort.index;
          // outID, outIndex, inID, inIndex
          this.onDeleteConnection(
            outputID, outputIndex, inputID, inputIndex);
          this.justErased = true;
          return true;
        }
      }
    }
    return false;
  }

  resetFromNewCoords (x, y) {
    if(this.x && this.y){
      this.xDiff = x - this.x;
      this.yDiff = y - this.y;
    }
    this.x = x;
    this.y = y;
  }

  resetOnRelease () {
    this.down = false;
    this.didDrag = false;
    this.radianDown = undefined;
    this.radianDiff = undefined;
  }

  releaseWithoutMoving (x, y) {
    this.params.inCircle.tapEvent();
    this.params.outCircle.tapEvent();

    // reset some Finger variables
    this.resetFromNewCoords(x, y);
    this.resetOnRelease();
    this.justErased = false;
    // find which Connection is being interacted with
    const touchedPort = this.params.touchedPort;
    if(touchedPort){
      // set all to hovered to false
      for(let n in this.params.connections){
        this.params.connections[n].hovered = false;
      }
      // then, find the interactive connection
      for (let n in this.params.connections){
        if (this.params.connections[n].inPort === touchedPort){
          this.params.connections[n].hovered = true;
        }
        else if (this.params.connections[n].outPort === touchedPort){
          this.params.connections[n].hovered = true;
        }
      }
    }
    this.clearCirclesTouchedHovered();
  }

  releaseEvent (x, y) {
    if (!this.didDrag) {
      this.releaseWithoutMoving(x, y);
      return;
    }
    this.resetFromNewCoords(x, y);
    this.resetOnRelease();
    const touchedPort = this.params.touchedPort;
    const hoveredPort = this.params.hoveredPort;
    if (touchedPort && hoveredPort && hoveredPort !== touchedPort){
      this.makeConnection(hoveredPort,touchedPort);
    }

    for (var n in this.params.connections){
      this.params.connections[n].hovered = false;
    }

    this.clearCirclesTouchedHovered();
  }

  clearCirclesTouchedHovered () {
    this.params.inCircle.clearTouchedHovered();
    this.params.outCircle.clearTouchedHovered();
    this.params.touchedPort = undefined;
    if (this.params.hoveredPort) {
      this.params.hoveredPort.hovered = false;
    }
    this.params.hoveredPort = undefined;
  }

  makeConnection (port1, port2) {
    let inPort;
    let outPort;
    if (port1.type === this.params.inType){
      inPort = port1;
      outPort = port2;
    }
    else{
      inPort = port2;
      outPort = port1;
    }
    const tempName = Connection.generateName(
      outPort.parent.id, outPort.index, inPort.parent.id, inPort.index);
    if (!this.params.connections[tempName]){
      const outputID = outPort.parent.id;
      const inputID = inPort.parent.id;
      // outID, outIndex, inID, inIndex
      this.onMakeConnection(outputID, outPort.index, inputID, inPort.index)
    }
  }

  dragEvent (x, y, justTouched) {
    this.resetFromNewCoords(x, y);
    this.didDrag = true;
    // determine what side of the screen we're on
    if (this.y > this.params.middleY) this.topSide = true;
    else this.topSide = false;
    if (!justTouched){
      this.inRadianPrev = this.inRadianNew;
      this.outRadianPrev = this.outRadianNew;
    }
    this.inRadianNew = this.radiansFromCenter(this.x, this.y, this.params.inType);
    this.outRadianNew = this.radiansFromCenter(this.x, this.y, this.params.outType);
    if (justTouched){
      this.inRadianPrev = this.inRadianNew;
      this.outRadianPrev = this.outRadianNew;
    }
  }

  getRadian (type) {
    return type === this.params.inType ? this.inRadianNew : this.outRadianNew;
  }

  radiansFromCenter (x, y, type) {
    let middleX = this.params.outCircle.centerX;
    let middleY = this.params.outCircle.centerY;
    if (type === this.params.inType){
      middleX = this.params.inCircle.centerX;
      middleY = this.params.inCircle.centerY;
    }
    const yDist = Math.abs(middleY - y);
    const xDist = Math.abs(middleX - x);
    if (x > middleX){
      if (y > middleY){
        // bottom right
        return Math.atan(yDist / xDist);
      }
      else if (y < middleY){
        // top right
        return Math.atan(xDist / yDist) + (Math.PI * 1.5);
      }
      else {
        // we're touching the y line
        return 0;
      }
    }
    else if (x < middleX){
      if (y > middleY){
        // bottom left
        return Math.atan(xDist / yDist) + (Math.PI * 0.5);
      }
      else if (y < middleY){
        // top left
        return Math.atan(yDist / xDist) + Math.PI;
      }
      else {
        // we're touching the y line
        return Math.PI;
      }
    }
    else {
      //we're touching the x line
      if (y > middleY){
        return Math.PI * 0.5;
      }
      else if (y < middleY){
        return Math.PI * 1.5;
      }
      else {
        // we're touching the middle
        return 0;
      }
    }
  }
}

export { Finger }
