import * as utils from './PatchbayUtils'
import { Circle } from './Circle'
import { Connection } from './Connection'
import { Finger } from './Finger'

const patchbayDefaults = {
  screenPercentage: 0.25,
  arcThicknessPercentage: 0.1,
  touchCircleRadiusScaler: 0.1,
  touchCircleThicknessScaler: 0.01,
  touchedLineWidth: 3,
  circleCenterXOffsetScaler: 0.3,
  circleCenterYOffsetScaler: 0.1
};

class Patchbay {
  constructor (context) {
    this.context = context;

    this.connections = {};

    this.inCircle = this.createCircle(utils.inType);
    this.outCircle = this.createCircle(utils.outType);
    this.finger = this.createFinger();

    this.width = undefined;
    this.height = undefined;
    this.middleX = undefined;
    this.middleY = undefined;
    this.screenSize = undefined;

    this.reqAnimFrame = undefined;
  }

  createFinger () {
    return new Finger(
      this.inCircle,
      this.outCircle,
      this.connections,
      (inPort, outPort) => this.createAndSaveConnection(inPort, outPort),
      (conn) => this.deleteConnection(conn)
    );
  }

  createCircle (type) {
    return new Circle(
      this.context,
      type,
      patchbayDefaults.screenPercentage,
      patchbayDefaults.arcThicknessPercentage,
      () => this.finger.touchedPort,
      () => this.finger.hoveredPort
    );
  }

  setSize (width, height) {
    this.width = width;
    this.height = height;
    this.updateScreenVariables();
    this.adjustChildrenToScreenSize();
  }

  adjustChildrenToScreenSize () {
    this.inCircle.adjustToScreenSize(this.screenSize);
    this.outCircle.adjustToScreenSize(this.screenSize);
    for(let i in this.connections){
      this.connections[i].adjustToScreenSize(this.screenSize);
    }
  }

  updateScreenVariables () {
    this.middleX = this.width / 2;
    this.middleY = this.height / 2;
    this.screenSize = Math.min(this.width, this.height);
    const xOffset = this.width * patchbayDefaults.circleCenterXOffsetScaler;
    const yOffset = this.height * patchbayDefaults.circleCenterYOffsetScaler;
    this.inCircle.x = this.middleX - xOffset;
    this.outCircle.x = this.middleX + xOffset;
    this.inCircle.y = this.middleY - yOffset;
    this.outCircle.y = this.middleY + yOffset;
  }

  drawLoop () {
    if (this.reqAnimFrame) {
      cancelAnimationFrame(this.reqAnimFrame);
      this.reqAnimFrame = undefined;
    }
    this.finger.update();
    this.inCircle.update();
    this.outCircle.update();
    for(let i in this.connections){
      this.connections[i].update();
    }

    this.context.save();
    this.clearCanvas();

    this.inCircle.drawArcs();
    this.outCircle.drawArcs();

    this.inCircle.drawNames();
    this.outCircle.drawNames();

    this.inCircle.drawPorts();
    this.outCircle.drawPorts();

    for(let i in this.connections){
      this.connections[i].draw();
    }

    const circleRadius = this.screenSize * patchbayDefaults.touchCircleRadiusScaler;
    const circleThickness = this.screenSize * patchbayDefaults.touchCircleThicknessScaler;
    this.drawTouchLine();
    this.drawTouchedPort(circleRadius, circleThickness);
    this.drawHoveredPort(circleRadius, circleThickness);

    this.context.restore();
    this.reqAnimFrame = requestAnimationFrame(() => this.drawLoop());
  }

  drawTouchedPort (circleRadius, circleThickness) {
    if (!this.finger.touchedPort) {
      return;
    }
    this.context.save();
    utils.drawArc(
      this.context,
      this.finger.touchedPort.x, this.finger.touchedPort.y,
      circleRadius, 0, utils.PI2, circleThickness,
      utils.colors.white(), undefined
    );
    this.context.restore();
  }

  drawTouchLine () {
    if (!this.finger.touchedPort) {
      return;
    }
    this.context.save();
    utils.drawLine(
      this.context,
      this.finger.touchedPort.x, this.finger.touchedPort.y,
      this.finger.x, this.finger.y,
      patchbayDefaults.touchedLineWidth, utils.colors.green()
    );
    this.context.restore();
    // then two smaller filled in circles to cover the tips of the line
    this.context.save();
    utils.drawArc(
      this.context,
      this.finger.touchedPort.x, this.finger.touchedPort.y,
      patchbayDefaults.touchedLineWidth * 2, 0, utils.PI2, 0,
      undefined, utils.colors.white()
    );
    utils.drawArc(
      this.context,
      this.finger.x, this.finger.y,
      patchbayDefaults.touchedLineWidth * 2, 0, utils.PI2, 0,
      undefined, utils.colors.white()
    );
    this.context.restore();
  }

  drawHoveredPort (circleRadius, circleThickness) {
    if (!this.finger.hoveredPort) {
      return;
    }
    this.context.save();
    utils.drawArc(
      this.context,
      this.finger.hoveredPort.x, this.finger.hoveredPort.y,
      circleRadius, 0, utils.PI2, circleThickness,
      utils.colors.green(), undefined
    );
    this.context.restore();
  }

  clearCanvas() {
    this.context.fillStyle = utils.colors.background();
    this.context.fillRect(0, 0, this.width, this.height);
  }

  createAndSaveConnection (inPort, outPort) {
    this.saveConnection(this.createConnection(inPort, outPort));
  }

  createConnection (inPort, outPort) {
    const connection = new Connection(this.context, inPort, outPort);
    connection.adjustToScreenSize(this.screenSize);
    return connection;
  }

  saveConnection (connection) {
    this.deleteConnection(connection);
    this.connections[connection.name] = connection;
  }

  deleteConnection (conn) {
    this.deleteConnectionByName(conn.name);
  }

  deleteConnectionByName (name) {
    if (this.connections[name]) {
      delete this.connections[name];
    }
  }

  handleUserEvent (type, x, y) {
    if (type === utils.userEventTypes.touch) {
      const justTouched = true;
      this.finger.moveEvent(x, y, justTouched);
      this.finger.touchEvent();
    }
    else if (type === utils.userEventTypes.move) {
      const justTouched = false;
      this.finger.moveEvent(x, y, justTouched);
    }
    else if (type === utils.userEventTypes.release) {
      this.finger.releaseEvent(x, y);
    }
  }
}

export { Patchbay }
