import * as utils from './DrawUtils'

const portDefaults = {
  wobbleStep: 0.07,
  wobbleAmount: 3,
  circleSizeScaler: 0.4,
  circleSizeDrawThresh: 2,
  lineWidthScaler: 0.4,
  minCircleSize: 1,
  minStrokeWidth: 4,
  fillTextFontSizeScaler: 0.2,
  radOffsetScaler: 0.05,
  fontSizeScaler: 0.4,
  touchingScaler: 1.0
};

class Port {
  constructor (params, parent, type, index) {
    this.params = params;
    this.context = this.params.context;

    this._parent = parent;
    this._type = type;
    this._index = index;
    this._name = "";

    this.connections = {};

    this._x = 0;
    this._y = 0;
    this.relX = 0;
    this.relY = 0;

    this._touched = false;
    this._hovered = false;

    this.sizeScaler = 0;
    this.circleRad = undefined;
    this._arcLineWidth = undefined;

    this.wobbleCounter = Math.random() * utils.PI2;
    this.wobbleStep = portDefaults.wobbleStep;
    this.wobbleAmount = portDefaults.wobbleAmount;

    this.circleSize = undefined;
    this.lineWidth = undefined;
    this.strokeStyle = undefined;
    this.fillStyle = undefined;
    this.shouldDraw = false;

    this.radOffset = undefined;

    // the visible flag is used by Cords to determine if they should be shown
    this._visible = false;
  }

  // GETTERS

  get visible () { return this._visible }

  get x () { return this._x }

  get y () { return this._y }

  get touched () { return this._touched; }

  get hovered () { return this._hovered; }

  get name () { return this._name; }

  get index () { return this._index; }

  get type () { return this._type; }

  get parent () { return this._parent; }

  get size () { return this._arcLineWidth; }

  // SETTERS

  set touched (state) { this._touched = state; }

  set hovered (state) { this._hovered = state; }

  set name (name) { this._name = name; }

  update (sizeScaler, radLocation, isVisible) {
    this.updateFrameSettings(sizeScaler, isVisible);
    this.wobbleCounter = (this.wobbleCounter + this.wobbleStep) % utils.PI2;
    // Ports need an absolute coordinate on the screen
    // because their position is used to determine when they are being
    // interacted with
    this._x = this.circleRad * Math.cos(radLocation) + this.relX;
    this._y = this.circleRad * Math.sin(radLocation) + this.relY;
  }

  onScreenUpdate (rad, lineWidth, relX, relY) {
    this.circleRad = rad;
    this._arcLineWidth = lineWidth;
    this.relX = relX;
    this.relY = relY;
  }

  updateFrameSettings (sizeScaler, isVisible) {
    this.sizeScaler = sizeScaler;
    const tempSize = this._arcLineWidth * this.sizeScaler * portDefaults.circleSizeScaler;
    this._visible = isVisible;
    if(this._visible && tempSize > portDefaults.circleSizeDrawThresh){
      this.circleSize = tempSize;
      if(this.params.touchedPort && this.params.touchedPort.type !== this._type) {
        this.circleSize += (Math.sin(this.wobbleCounter) * this.wobbleAmount);
      }
      this.circleSize = Math.max(this.circleSize, portDefaults.minCircleSize);

      // colors
      this.strokeStyle = undefined;
      if(tempSize > portDefaults.minStrokeWidth) {
        this.strokeStyle = utils.colors.background();
      }
      this.fillStyle = this._parent.color();
      this.lineWidth = Math.floor(tempSize * portDefaults.lineWidthScaler);
    }

    // settings for drawing the name
    this.radOffset = this._parent.radius - (this.params.screenSize * portDefaults.radOffsetScaler);
    this.fontSize = Math.floor(this.sizeScaler * this._arcLineWidth * portDefaults.fontSizeScaler);
  }

  draw () {;
    if(this._visible){
      this.context.save();
      utils.drawArc(
        this.context,
        0, this.circleRad,
        this.circleSize, 0, utils.PI2,
        this.lineWidth, this.strokeStyle, this.fillStyle
      )
      this.context.restore();
    }
  }

  drawName () {
    this.context.save();
    this.context.translate(this.radOffset, 0);

    if(this._x < this._parent.parent.centerX){
      this.context.textAlign = 'left';
      this.context.rotate(Math.PI);
    }
    else{
      this.context.textAlign = 'right';
    }
    this.context.font = this.fontSize + 'px Helvetica';
    if(this._touched || this._hovered) {
      this.context.fillStyle = utils.colors.white();
    }
    else {
      this.context.fillStyle = this._parent.color();
    }

    this.context.fillText(
      this._name, 0, this.fontSize * portDefaults.fillTextFontSizeScaler);
    this.context.restore();
  }

  isTouching (x, y) {
    const dist = utils.getDistance(this._x, this._y, x, y);
    if (this._visible && dist < this.circleSize) {
      return true;
    }
    return false;
  }
}

export { Port }
